INSERT INTO `sys_industry_2002` VALUES (1773, '052', '  林业服务业', '05', 2, '    指为林业生产服务的病虫害的防治、森林防火等各种支持性活动。');
INSERT INTO `sys_industry_2002` VALUES (1774, '053', '  畜牧服务业', '05', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1775, '054', '  渔业服务业', '05', 2, '    指对渔业生产活动进行的各种支持性服务活动，包括鱼苗及鱼种场、水产良种场和水产增殖场等进行的活动。');
INSERT INTO `sys_industry_2002` VALUES (1776, '061', '     烟煤和无烟煤的开采洗选', '06', 2, '    指对地下或露天烟煤、无烟煤的开采，以及对采出的烟煤、无烟煤及其他硬煤进行洗选、分级等提高质量的活动。');
INSERT INTO `sys_industry_2002` VALUES (1777, '062', '     褐煤的开采洗选', '06', 2, '    指对褐煤---煤化程度较低的一种燃料的地下或露天开采，以及对采出的褐煤进行洗选、分级等提高质量的活动。');
INSERT INTO `sys_industry_2002` VALUES (1778, '069', '     其他煤炭采选', '06', 2, '    指对生长在古生代地层中的含碳量低、灰分高的煤炭资源（如石煤、泥炭）的开采。');
INSERT INTO `sys_industry_2002` VALUES (1779, '071', '  天然原油和天然气开采', '07', 2, '    指在陆地或海洋，对天然原油、液态或气态天然气的开采，对煤矿瓦斯气（煤层气）的开采，为运输目的所进行的天然气液化和从天然气田气体中生产液化烃的活动。还包括对含沥青的页岩或油母页岩矿的开采，以及对焦油沙矿进行的同类作业。');
INSERT INTO `sys_industry_2002` VALUES (1780, '079', '  与石油和天然气开采有关的服务活动', '07', 2, '   指为石油和天然气开采提供的服务活动。');
INSERT INTO `sys_industry_2002` VALUES (1781, '081', '  铁矿采选', '08', 2, '    指对铁矿石的采矿、选矿活动。');
INSERT INTO `sys_industry_2002` VALUES (1782, '089', '  其他黑色金属矿采选', '08', 2, '  指对锰矿、铬矿等钢铁工业黑色金属辅助原料矿的采矿、选矿活动。');
INSERT INTO `sys_industry_2002` VALUES (1783, '091', '  常用有色金属矿采选', '09', 2, '    指对铜、铅锌、镍钴、锡、锑、铝、镁、汞、镉、铋等常用有色金属矿的采选活动。');
INSERT INTO `sys_industry_2002` VALUES (1784, '092', '  贵金属矿采选', '09', 2, '    指对在地壳中含量极少的金、银和铂族元素（铂、铱、锇、钌、钯、铑）矿的采选活动。');
INSERT INTO `sys_industry_2002` VALUES (1785, '093', '  稀有稀土金属矿采选', '09', 2, '     指对在自然界中含量较小，分布稀散或难以从原料中提取，以及研究和使用较晚的金属矿开采、精选活动。');
INSERT INTO `sys_industry_2002` VALUES (1786, '101', '  土砂石开采', '10', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1787, '102', '  化学矿采选', '10', 2, '    指对化学矿和肥料矿物的开采。');
INSERT INTO `sys_industry_2002` VALUES (1788, '103', '  采盐', '10', 2, '    指通过以海水（含沿海浅层地下卤水）为原料晒制，或以钻井汲取地下卤水，或注水溶解地下岩盐为原料，经真空蒸发干燥，以及从盐湖中采掘制成的以氯化钠为主要成分的盐产品的开采、粉碎和筛选活动。');
INSERT INTO `sys_industry_2002` VALUES (1789, '109', '    石棉及其他非金属矿采选', '10', 2, '    指对石棉、石墨、贵重宝石、金刚石、天然磨料，以及其他矿石的开采。');
INSERT INTO `sys_industry_2002` VALUES (1790, '110', '  其他采矿业  ', '11', 2, '    指对地热资源、矿泉水资源以及其他未列明的自然资源的开采活动。但不包括利用这些资源建立的热电厂和矿泉水厂。');
INSERT INTO `sys_industry_2002` VALUES (1791, '131', '  谷物磨制', '13', 2, '    也称粮食加工，指将稻子、谷子、小麦、高粱等谷物去壳、碾磨及精加工的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1792, '132', '  饲料加工     ', '13', 2, '    指适用于农场、农户饲养牲畜、家禽的饲料生产加工活动，包括宠物食品的生产。');
INSERT INTO `sys_industry_2002` VALUES (1793, '133', '  植物油加工', '13', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1794, '134', '  制糖', '13', 2, '    指以甘蔗、甜菜为原料制作成品糖，以及以原糖或砂糖为原料精炼加工各种精制糖的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1795, '135', '  屠宰及肉类加工', '13', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1796, '136', '  水产品加工', '13', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1797, '137', '  蔬菜、水果和坚果加工', '13', 2, '    指用脱水、干制、冷藏、冷冻、腌制等方法，对蔬菜、水果、坚果的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (1798, '139', '    其他农副食品加工  ', '13', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1799, '141', '  焙烤食品制造', '14', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1800, '142', '  糖果、巧克力及蜜饯制造', '14', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1801, '143', '  方便食品制造', '14', 2, '    指以米、面、杂粮等为主要原料加工制成，只需简单烹制即可作为主食的具有食用简便、携带方便，易于储藏等特点的食品制造。');
INSERT INTO `sys_industry_2002` VALUES (1802, '144', '  液体乳及乳制品制造', '14', 2, '    指以牛、羊乳为主要原料，经分级、净乳、杀菌、浓缩、干燥、发酵等加工制成的液体乳及乳制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (1803, '145', '     罐头制造', '14', 2, '    指将符合要求的原料经处理、分选、修整、烹调（或不经烹调）、装罐、密封、杀菌、冷却（或无菌包装）等罐头生产工艺制成的，达到商业无菌要求，并可以在常温下储存的罐头食品的制造。');
INSERT INTO `sys_industry_2002` VALUES (1804, '146', '    调味品、发酵制品制造', '14', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1805, '149', '    其他食品制造', '14', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1806, '151', '  酒精制造', '15', 2, '    指用玉米、小麦、薯类等淀粉质原料或用糖蜜等含糖质原料，经蒸煮、糖化、发酵及蒸馏等工艺制成的酒精产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (1807, '152', '  酒的制造', '15', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1808, '153', '  软饮料制造', '15', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1809, '154', '  精制茶加工', '15', 2, '    指对毛茶或半成品原料茶进行筛分、轧切、风选、干燥、匀堆、拼配等精制加工茶叶的生产。\n');
INSERT INTO `sys_industry_2002` VALUES (1810, '161', '  烟叶复烤', '16', 2, '    指在原烟（初烤）基础上进行第二次烟叶水分调整的活动。');
INSERT INTO `sys_industry_2002` VALUES (1811, '162', '  卷烟制造', '16', 2, '    指各种卷烟生产，但不包括生产烟用滤嘴棒的纤维丝束原料的制造。');
INSERT INTO `sys_industry_2002` VALUES (1812, '169', '    其他烟草制品加工', '16', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1813, '171', '  棉、化纤纺织及印染精加工', '17', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1814, '172', '  毛纺织和染整精加工', '17', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1815, '173', '  麻纺织', '17', 2, '    指以苎麻、亚麻、大麻等为主要原料进行的纺、织生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1816, '174', '  丝绢纺织及精加工', '17', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1817, '175', '  纺织制成品制造', '17', 2, '    指以棉、化纤、毛以及各种麻和丝纺织制成品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1818, '176', '  针织品、编织品及其制品制造', '17', 2, '    指纯粹由手工织成或钩成，或由机器针织、钩针编织成形的制品制造。');
INSERT INTO `sys_industry_2002` VALUES (1819, '181', '  纺织服装制造', '18', 2, '    指以纺织面料为主要原料，经裁剪后缝制各种男、女服装，以及儿童成衣的活动。包括非自产原料制作的服装，以及固定生产地点的服装制做。');
INSERT INTO `sys_industry_2002` VALUES (1820, '182', '  纺织面料鞋的制造', '18', 2, '    指用各种纺织面料、木材、棕草等原料缝制、模压或编制各种鞋的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1821, '183', '  制帽', '18', 2, '    指用各种纺织原料、皮革和毛皮原料，经剪裁、缝制或压制帽子的制作，以及针织或钩针编织成毛线帽的活动。');
INSERT INTO `sys_industry_2002` VALUES (1822, '191', '  皮革鞣制加工', '19', 2, '    指动物生皮经脱毛、鞣制等物理和化学方法加工，再经涂饰和整理，制成具有不易腐烂、柔韧、透气等性能的皮革生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1823, '192', '  皮革制品制造', '19', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1824, '193', '  毛皮鞣制及制品加工', '19', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1825, '194', '  羽毛(绒)加工及制品制造', '19', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1826, '201', '  锯材、木片加工', '20', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1827, '202', '  人造板制造', '20', 2, '    指用木材及其剩余物、棉秆、甘蔗渣和芦苇等植物纤维为原料，加工成符合国家标准的胶合板、纤维板、刨花板、细木工板和木丝板等产品的生产，以及人造板二次加工装饰板的制造。');
INSERT INTO `sys_industry_2002` VALUES (1828, '203', '  木制品制造', '20', 2, '    指以木材为原料加工成建筑用木料和木材组件、木容器、软木制品及其他木制品的生产活动。但不包括木质家具的制造。');
INSERT INTO `sys_industry_2002` VALUES (1829, '204', '    竹、藤、棕、草制品制造', '20', 2, '     指除木材以外，以 竹、藤、棕、草等天然植物为原料生产制品的活动。但不包括家具的制造。');
INSERT INTO `sys_industry_2002` VALUES (1830, '211', '  木质家具制造', '21', 2, '    指以天然木材和木质人造板为主要材料，配以其他辅料（如油漆、贴面材料、玻璃、五金配件等）制作各种家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1831, '212', '  竹、藤家具制造', '21', 2, '    指以竹材和藤材为主要材料，配以其他辅料制作各种家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1832, '213', '  金属家具制造', '21', 2, '    指支(框)架及主要部件以铸铁、钢材、钢板、钢管、合金等金属为主要材料，结合使用木、竹、塑等材料，配以人造革、尼龙布、泡沫塑料等其他辅料制作各种家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1833, '214', '  塑料家具制造', '21', 2, '    指用塑料管、板、异型材加工或用塑料、玻璃钢（即增强塑料）直接在模具中成型的家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1834, '219', '  其他家具制造', '21', 2, '    指主要由弹性材料(如弹簧、蛇簧、拉簧等)和软质材料(如棕丝、棉花、乳胶海绵、泡沫塑料等)，辅以绷结材料(如绷绳、绷带、麻布等)和装饰面料及饰物(如棉、毛、化纤织物及牛皮、羊皮、人造革等)制成的各种软家具；以玻璃为主要材料，辅以木材或金属材料制成的各种玻璃家具，以及其他未列明的原材料制作各种家具的活动。\n');
INSERT INTO `sys_industry_2002` VALUES (1835, '221', '  纸浆制造', '22', 2, '    指经机械或化学方法加工纸浆的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1836, '222', '  造纸', '22', 2, '    指用纸浆或其他原料（如矿渣棉、云母、石棉等）悬浮在流体中的纤维，经过造纸机或其他设备成型，或手工操作而成的纸及纸板的制造活动。');
INSERT INTO `sys_industry_2002` VALUES (1837, '223', '  纸制品制造', '22', 2, '    指用纸及纸板为原料，进一步加工制成纸制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1838, '231', '  印刷', '23', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1839, '232', '  装订及其他印刷服务活动', '23', 2, '    指专门企业从事的装订、压印媒介制造等与印刷有关的服务活动。');
INSERT INTO `sys_industry_2002` VALUES (1840, '233', '  记录媒介的复制', '23', 2, '    指将母带、母盘上的信息进行批量翻录的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1841, '241', '  文化用品制造', '24', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1842, '242', '  体育用品制造', '24', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1843, '243', '  乐器制造', '24', 2, '    指中国民族乐器、西乐器等各种乐器及乐器零部件和配套产品的制造，但不包括玩具乐器的制造。');
INSERT INTO `sys_industry_2002` VALUES (1844, '244', '  玩具制造', '24', 2, '     指以儿童为主要使用者，具备娱乐性、教育性和安全性三个基本特征的娱乐器具的制造。 ');
INSERT INTO `sys_industry_2002` VALUES (1845, '245', '  游艺器材及娱乐用品制造', '24', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1846, '251', '  精炼石油产品的制造', '25', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1847, '252', '  炼焦', '25', 2, '  指主要从硬煤和褐煤中生产焦炭、干馏炭及煤焦油或沥青等副产品的炼焦炉的操作活动。');
INSERT INTO `sys_industry_2002` VALUES (1848, '253', '  核燃料加工', '25', 2, '  指从沥青铀矿或其他含铀矿石中提取铀、浓缩铀的生产，对铀金属的冶炼、加工的生产，以及其他放射性元素、同位素标记、核反应堆燃料元件的制造。还包括核废物处置活动。');
INSERT INTO `sys_industry_2002` VALUES (1849, '261', '  基础化学原料制造', '26', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1850, '262', '  肥料制造', '26', 2, '     指化学肥料、有机肥料及微生物肥料的制造。');
INSERT INTO `sys_industry_2002` VALUES (1851, '263', '  农药制造', '26', 2, '     指用于防治农业、林业作物的病、虫、草、鼠和其他有害生物，调节植物生长的各种化学农药、微生物农药、生物化学农药，以及仓储、农林产品的防蚀、河流堤坝、铁路、机场、建筑物及其他场所用药的原药和制剂的生产。');
INSERT INTO `sys_industry_2002` VALUES (1852, '264', '  涂料、油墨、颜料及类似产品制造', '26', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1853, '265', '  合成材料制造', '26', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1854, '266', '  专用化学产品制造', '26', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1855, '267', '  日用化学产品制造', '26', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1856, '271', '  化学药品原药制造', '27', 2, '    指供进一步加工药品制剂所需的原药生产。');
INSERT INTO `sys_industry_2002` VALUES (1857, '272', '  化学药品制剂制造', '27', 2, '    指直接用于人体疾病防治、诊断的化学药品制剂的制造。');
INSERT INTO `sys_industry_2002` VALUES (1858, '273', '  中药饮片加工', '27', 2, '    指对采集的天然或人工种植、养殖的动物和植物及中草药进行加工、处理的活动。');
INSERT INTO `sys_industry_2002` VALUES (1859, '274', '  中成药制造', '27', 2, '    指直接用于人体疾病防治的传统药的加工生产。');
INSERT INTO `sys_industry_2002` VALUES (1860, '275', '  兽用药品制造', '27', 2, '   指用于动物疾病防治医药的制造。');
INSERT INTO `sys_industry_2002` VALUES (1861, '276', '  生物、生化制品的制造', '27', 2, '   指利用生物技术生产生物化学药品、基因工程药物的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1862, '277', '    卫生材料及医药用品制造', '27', 2, '    指卫生材料、外科敷料、药用包装材料以及其他内、外科用医药制品的制造。 ');
INSERT INTO `sys_industry_2002` VALUES (1863, '281', '  纤维素纤维原料及纤维制造', '28', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1864, '282', '  合成纤维制造', '28', 2, '    指以石油、天然气、煤等为主要原料，用有机合成的方法制成单体，聚合后经纺丝加工生产纤维的活动。');
INSERT INTO `sys_industry_2002` VALUES (1865, '291', '  轮胎制造', '29', 2, NULL);
INSERT INTO `sys_industry_2002` VALUES (1866, '292', '  橡胶板、管、带的制造', '29', 2, '    指用未硫化的、硫化的或硬质橡胶生产橡胶板状、片状、管状、带状、棒状和异型橡胶制品的活动，以及以橡胶为主要成分，用橡胶灌注、涂层、覆盖或层叠的纺织物、纱绳、钢丝（钢缆）等制作的传动带或输送带的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1867, '293', '  橡胶零件制造', '29', 2, '    指各种用途的橡胶异形制品、橡胶零配件制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (1868, '294', '  再生橡胶制造', '29', 2, '    指用废橡胶生产再生橡胶的活动。');
