INSERT INTO `sys_industry_2002` VALUES (1872, '301', '  塑料薄膜制造', '30', 2, '    指用于农业覆盖，工业、商业及日用包装薄膜的制造。');
INSERT INTO `sys_industry_2002` VALUES (1873, '302', '  塑料板、管、型材的制造', '30', 2, '    指各种塑料板、管及管件、棒材、薄片等的生产，以及以聚氯乙烯为主要原料，经连续挤出成型的塑料异型材的生产。\n');
