INSERT INTO `sys_industry_2002` VALUES (1889, '322', '  炼钢', '32', 2, '    指利用不同来源的氧（如空气、氧气）来氧化炉料（主要是生铁）所含杂质的金属提纯过程，称为炼钢活动。');
INSERT INTO `sys_industry_2002` VALUES (1890, '323', '  钢压延加工', '32', 2, '    指通过塑性加工使连铸坯、钢锭产生塑性变形，制成具有一定形状尺寸的钢材产品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1891, '324', '  铁合金冶炼', '32', 2, '    指通过热轧、冷加工、锻压和挤压等塑性加工使连铸坯、钢锭产生塑性变形，制成具有一定形状尺寸的钢材产品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1892, '331', '  常用有色金属冶炼', '33', 2, '    指通过熔炼、精炼、电解或其他方法从有色金属矿、废杂金属料等有色金属原料中提炼常用有色金属的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (1893, '332', '  贵金属冶炼', '33', 2, '    指对金、银及铂族金属的提炼活动。');
