INSERT INTO `sys_industry_2002` VALUES (1958, '431', '  金属废料和碎屑的加工处理', '43', 2, '    指从各种废料[包括固体废料、废水（液）、废气等]中回收，并使之便于转化为新的原材料，或适于进一步加工为金属原料的金属废料和碎屑的再加工处理活动。');
INSERT INTO `sys_industry_2002` VALUES (1959, '432', '  非金属废料和碎屑的加工处理', '43', 2, '    指从各种废料[包括固体废料、废水（液）、废气等]中回收，或经过分类，使其适于进一步加工为新原料的非金属废料和碎屑的再加工处理活动。');
