INSERT INTO `sys_industry_2002` VALUES (1962, '443', '  热力生产和供应', '44', 2, '    指利用煤炭、油、燃气等能源，通过锅炉等装置生产蒸汽和热水，或外购蒸汽、热水进行供应销售、供热设施的维护和管理的活动。');
INSERT INTO `sys_industry_2002` VALUES (1963, '450', '  燃气生产和供应业', '45', 2, '    指利用煤炭、油、燃气等能源生产燃气，或外购液化石油气、天然气等燃气，并进行输配，向用户销售燃气的活动，以及对煤气、液化石油气、天然气输配及使用过程中的维修和管理活动。但不包括专门从事罐装液化石油气零售业务的活动。');
INSERT INTO `sys_industry_2002` VALUES (1964, '461', '  自来水的生产和供应', '46', 2, '    指将天然水（地下水、地表水）经过蓄集、净化达到生活饮用水或其他用水标准，并向居民家庭、企业和其他用户供应的活动。');
INSERT INTO `sys_industry_2002` VALUES (1965, '462', '  污水处理及其再生利用', '46', 2, '    指对污水的收集、处理及净化后的再利用活动。');
INSERT INTO `sys_industry_2002` VALUES (1966, '469', '  其他水的处理、利用与分配', '46', 2, '    指将海水淡化处理，达到可以使用标准的生产活动，以及对雨水、微咸水等类似水进行收集、处理和利用活动。');
