INSERT INTO `sys_industry_2002` VALUES (2213, '0939', '    其他稀有金属矿采选', '093', 3, '    指对稀有轻金属矿、稀有高熔点金属矿、稀散金属矿，以及其他稀有金属矿的采选活动。');
INSERT INTO `sys_industry_2002` VALUES (2214, '1011', '    石灰石、石膏开采', '101', 3, '   指对石灰、石膏，以及石灰石助熔剂的开采活动。');
INSERT INTO `sys_industry_2002` VALUES (2215, '1012', '    建筑装饰用石开采', '101', 3, '   指通常在采石场切制加工各种纪念碑及建筑用石料的活动。');
INSERT INTO `sys_industry_2002` VALUES (2216, '1013', '    耐火土石开采', '101', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2217, '1019', '        粘土及其他土砂石开采', '101', 3, '    指用于建筑、陶瓷等方面的粘土开采，以及用于铺路和建筑材料的石料、石渣、砂的开采。');
INSERT INTO `sys_industry_2002` VALUES (2218, '1020', '  化学矿采选', '102', 3, '    指对化学矿和肥料矿物的开采。');
INSERT INTO `sys_industry_2002` VALUES (2219, '1030', '  采盐', '103', 3, '    指通过以海水（含沿海浅层地下卤水）为原料晒制，或以钻井汲取地下卤水，或注水溶解地下岩盐为原料，经真空蒸发干燥，以及从盐湖中采掘制成的以氯化钠为主要成分的盐产品的开采、粉碎和筛选活动。');
INSERT INTO `sys_industry_2002` VALUES (2220, '1091', '        石棉、云母矿采选', '109', 3, '             ');
INSERT INTO `sys_industry_2002` VALUES (2221, '1092', '        石墨、滑石采选', '109', 3, '    指对天然石墨、 滑石的开采。');
INSERT INTO `sys_industry_2002` VALUES (2222, '1093', '    宝石、玉石开采', '109', 3, '    指对贵重宝石、玉石、彩石的开采。');
INSERT INTO `sys_industry_2002` VALUES (2223, '1099', '    其他非金属矿采选', '109', 3, '            ');
INSERT INTO `sys_industry_2002` VALUES (2224, '1100', '  其他采矿业  ', '110', 3, '    指对地热资源、矿泉水资源以及其他未列明的自然资源的开采活动。但不包括利用这些资源建立的热电厂和矿泉水厂。');
INSERT INTO `sys_industry_2002` VALUES (2225, '1310', '  谷物磨制', '131', 3, '    也称粮食加工，指将稻子、谷子、小麦、高粱等谷物去壳、碾磨及精加工的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2226, '1320', '  饲料加工     ', '132', 3, '    指适用于农场、农户饲养牲畜、家禽的饲料生产加工活动，包括宠物食品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2227, '1331', '    食用植物油加工', '133', 3, '    指用各种食用植物油料生产油脂，以及精制食用油的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2228, '1332', '    非食用植物油加工', '133', 3, '    指用各种非食用植物油料生产油脂的活动。                                                                ');
INSERT INTO `sys_industry_2002` VALUES (2229, '1340', '  制糖', '134', 3, '    指以甘蔗、甜菜为原料制作成品糖，以及以原糖或砂糖为原料精炼加工各种精制糖的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2230, '1351', '    畜禽屠宰', '135', 3, '    指对各种畜、禽进行宰杀，以及鲜肉冷冻等保鲜活动，但不包括商业冷藏。');
INSERT INTO `sys_industry_2002` VALUES (2231, '1352', '    肉制品及副产品加工', '135', 3, '    指主要以各种畜、禽肉为原料加工成熟肉制品，以及畜、禽副产品的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2232, '1361', '    水产品冷冻加工', '136', 3, '     指为了保鲜，将海水、淡水养殖或捕捞的鱼类、虾类、甲壳类、贝类、藻类等水生动物或植物进行的冷冻加工活动，但不包括商业冷藏。');
INSERT INTO `sys_industry_2002` VALUES (2233, '1362', '    鱼糜制品及水产品干腌制加工', '136', 3, '    指鱼糜制品制造，以及水产品的干制、腌制等加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2234, '1363', '    水产饲料制造', '136', 3, '    指用鱼骨、虾、贝等水产品生产饲料的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2235, '1364', '    鱼油提取及制品的制造', '136', 3, '    指从鱼或鱼肝中提取油脂，并生产制品的活动。');
INSERT INTO `sys_industry_2002` VALUES (2236, '1369', '    其他水产品加工', '136', 3, '    指对水生动物或水生植物进行的其他加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2237, '1370', '  蔬菜、水果和坚果加工', '137', 3, '    指用脱水、干制、冷藏、冷冻、腌制等方法，对蔬菜、水果、坚果的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2238, '1391', '        淀粉及淀粉制品的制造', '139', 3, '     指用玉米、薯类、豆类及其他植物原料制作淀粉和淀粉制品的生产。还包括以淀粉为原料，经酶法或酸法转换得到的糖品生产。\n');
INSERT INTO `sys_industry_2002` VALUES (2239, '1392', '        豆制品制造', '139', 3, '    指以大豆、小豆、绿豆、豌豆、蚕豆等豆类为主要原料，经加工制成食品的活动。');
INSERT INTO `sys_industry_2002` VALUES (2240, '1393', '        蛋品加工          ', '139', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2241, '1399', '        其他未列明的农副食品加工  ', '139', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2242, '1411', '    糕点、面包制造', '141', 3, '    指用米粉、面粉、豆粉为主要原料，配以辅料，经成型、油炸、烤制而成的各种食品生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2243, '1419', '    饼干及其他焙烤食品制造', '141', 3, '    指以面粉（或糯米粉）、糖和油脂为主要原料，配以奶制品、蛋制品等辅料，经成型、焙烤制成的各种饼干，以及用薯类、谷类、豆类等制做的各种易于保存、食用方便、口感酥脆的焙烤食品生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2244, '1421', '    糖果、巧克力制造', '142', 3, '    指以砂糖、葡萄糖浆或饴糖为主要原料，加入油脂、乳品、胶体、果仁、香料、食用色素等辅料制成甜味块状食品--糖果的制造，以及以浆状、粉状或块状可可、可可脂、可可酱、砂糖、乳品等为主要原料加工制成巧克力及巧克力制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2245, '1422', '    蜜饯制作', '142', 3, '    指以水果、坚果、果皮及植物的其他部分制做糖果蜜饯的活动。');
INSERT INTO `sys_industry_2002` VALUES (2246, '1431', '        米、面制品制造', '143', 3, '   指以大米、面粉为原料，经粗加工制成，未经烹制的各类米面制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2247, '1432', '    速冻食品制造', '143', 3, '    指以米、面、杂粮等为主要原料，以肉类、蔬菜等为辅料，经加工制成各类烹制或未烹制的主食品后，立即采用速冻工艺制成的，并可以在冻结条件下运输储存及销售的各类主食品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2248, '1439', '        方便面及其他方便食品制造', '143', 3, '    指用米、面、杂粮等为主要原料加工制成的，可以直接食用或只需简单蒸煮即可作为主食的各种方便主食品的生产，以及其他未列明的方便食品制造。');
INSERT INTO `sys_industry_2002` VALUES (2249, '1440', '  液体乳及乳制品制造', '144', 3, '    指以牛、羊乳为主要原料，经分级、净乳、杀菌、浓缩、干燥、发酵等加工制成的液体乳及乳制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2250, '1451', '        肉、禽类罐头制造', '145', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2251, '1452', '        水产品罐头制造', '145', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2252, '1453', '        蔬菜、水果罐头制造', '145', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2253, '1459', '        其他罐头食品制造', '145', 3, '    指婴幼儿辅助食品类罐头、米面食品类罐头（如八宝粥罐头等）及上述未列明的罐头食品制造。');
INSERT INTO `sys_industry_2002` VALUES (2254, '1461', '    味精制造', '146', 3, '    指以淀粉或糖蜜为原料，经微生物发酵、提取、精制等工序制成的，谷氨酸钠含量在80％及以上的鲜味剂的生产。');
INSERT INTO `sys_industry_2002` VALUES (2255, '1462', '        酱油、食醋及类似制品的制造', '146', 3, '     指以大豆和（或）脱脂大豆，小麦和（或）麸皮为原料，经微生物发酵制成的各种酱油和酱类制品，以及以单独或混合使用各种含有淀粉、糖的物料或酒精，经微生物发酵酿制的酸性调味品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2256, '1469', '        其他调味品、发酵制品制造', '146', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2257, '1491', '        营养、保健食品制造', '149', 3, '    指主要适宜伤残者、老年人的，含肉、鱼、水果、蔬菜、奶、麦精、钙等均质配料的营养、保健食品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2258, '1492', '        冷冻饮品及食用冰制造', '149', 3, '    指以砂糖、乳制品、豆制品、蛋制品、油脂、果料和食用添加剂等经混合配制、加热杀菌、均质、老化、冻结（凝冻）而成的冷食饮品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2259, '1493', '        盐加工   ', '149', 3, '    指以原盐为原料，经过化卤、蒸发、洗涤、粉碎、干燥、脱水、筛分等工序，或在其中添加碘酸钾及调味品等加工制成盐产品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2260, '1494', '        食品及饲料添加剂制造', '149', 3, '     指增加或改善食品特色的化学品，以及补充动物饲料的营养成分和促进生长、防治疫病的制剂的生产。');
INSERT INTO `sys_industry_2002` VALUES (2261, '1499', '    其他未列明的食品制造', '149', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2262, '1510', '  酒精制造', '151', 3, '    指用玉米、小麦、薯类等淀粉质原料或用糖蜜等含糖质原料，经蒸煮、糖化、发酵及蒸馏等工艺制成的酒精产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2263, '1521', '    白酒制造', '152', 3, '    指以高粱等粮谷为主要原料，以大曲、小曲或麸曲及酒母等为糖化发酵剂，经蒸煮、糖化、发酵、蒸馏、陈酿、勾兑而制成的，酒精度(体积分数)在18％~ 60％的蒸馏酒产品的生产.');
INSERT INTO `sys_industry_2002` VALUES (2264, '1522', '    啤酒制造', '152', 3, '     指以麦芽（包括特种麦芽）为主要原料，加酒花，经酵母发酵酿制而成，含二氧化碳、起泡、低酒精度[体积分数2.5％~ 7.5％]的发酵酒产品的生产，以及啤酒专用原料麦芽的生产。');
INSERT INTO `sys_industry_2002` VALUES (2265, '1523', '    黄酒制造', '152', 3, '    指以稻米、黍米、黑米、小麦、玉米等为原料，加曲、酵母等糖化发酵剂发酵酿制而成的发酵酒产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2266, '1524', '    葡萄酒制造', '152', 3, '    指以新鲜葡萄或葡萄汁为原料，经全部或部分发酵酿制而成，酒精度(体积分数)等于或大于7％的发酵酒产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2267, '1529', '    其他酒制造', '152', 3, '    指除葡萄酒以外的其他果酒、配制酒以及上述未列明的其他酒产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2268, '1531', '    碳酸饮料制造', '153', 3, '  指在一定条件下充入二氧化碳气的饮用品制造。其成品中二氧化碳气的含量（20℃时的体积倍数）不低于2.0倍。');
INSERT INTO `sys_industry_2002` VALUES (2269, '1532', '    瓶（罐）装饮用水制造', '153', 3, '    指以地下矿泉水和符合生活饮用水卫生标准的水为水源加工制成的，密封于塑料瓶（罐）、玻璃瓶或其他容器中，不含任何添加剂，可直接饮用水的生产。');
INSERT INTO `sys_industry_2002` VALUES (2270, '1533', '       果菜汁及果菜汁饮料制造', '153', 3, '    指以新鲜或冷藏水果和蔬菜为原料，经加工制得的果菜汁液制品生产，以及在果汁或浓缩果汁、蔬菜汁中加入水、糖液、酸味剂等，经调制而成的可直接饮用的饮品（果汁含量不低于10％）的生产。\n');
INSERT INTO `sys_industry_2002` VALUES (2271, '1534', '    含乳饮料和植物蛋白饮料制造', '153', 3, '    指以鲜乳或乳制品为原料（经发酵或未经发酵），加入水、糖液等调制而成的可直接饮用的含乳饮品的生产；以及以蛋白质含量较高的植物的果实、种子或核果类、坚果类的果仁等为原料，在其加工制得的浆液中加入水、糖液等调制而成的可直接饮用的植物蛋白饮品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2272, '1535', '    固体饮料制造', '153', 3, '    指以糖、食品添加剂、果汁或植物抽提物等为原料，加工制成粉末状、颗粒状或块状制品[其成品水分(质量分数)不高于5％]的生产。');
INSERT INTO `sys_industry_2002` VALUES (2273, '1539', '        茶饮料及其他软饮料制造', '153', 3, '     指未列入上述各类的茶饮料、特殊用途饮料等其他软饮料的制造。     ');
INSERT INTO `sys_industry_2002` VALUES (2274, '1540', '  精制茶加工', '154', 3, '    指对毛茶或半成品原料茶进行筛分、轧切、风选、干燥、匀堆、拼配等精制加工茶叶的生产。\n');
INSERT INTO `sys_industry_2002` VALUES (2275, '1610', '  烟叶复烤', '161', 3, '    指在原烟（初烤）基础上进行第二次烟叶水分调整的活动。');
INSERT INTO `sys_industry_2002` VALUES (2276, '1620', '  卷烟制造', '162', 3, '    指各种卷烟生产，但不包括生产烟用滤嘴棒的纤维丝束原料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2277, '1690', '    其他烟草制品加工', '169', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2278, '1711', '    棉、化纤纺织加工', '171', 3, '    指以棉及棉型化学纤维为主要原料进行的纺纱、织布，以及用于织布和缝纫的线的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2279, '1712', '    棉、化纤印染精加工', '171', 3, '    指对非自产的棉和化学纤维纺织品进行漂白、染色、印花、轧光、起绒、缩水等工序的加工。');
INSERT INTO `sys_industry_2002` VALUES (2280, '1721', '    毛条加工', '172', 3, '    指以毛及毛型化学纤维为原料进行梳条的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2281, '1722', '    毛纺织', '172', 3, '    指以毛条及毛型化学纤维为原料进行的纺、织生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2282, '1723', '    毛染整精加工', '172', 3, '    指对非自产的毛纺织品进行漂白、染色、印花等工序的染整精加工。');
INSERT INTO `sys_industry_2002` VALUES (2283, '1730', '  麻纺织', '173', 3, '    指以苎麻、亚麻、大麻等为主要原料进行的纺、织生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2284, '1741', '    缫丝加工', '174', 3, '    指由蚕茧经过加工缫制成丝的活动。');
INSERT INTO `sys_industry_2002` VALUES (2285, '1742', '    绢纺和丝织加工', '174', 3, '    指以丝及化纤丝为主要原料进行的丝织生产活动。                                                \n');
INSERT INTO `sys_industry_2002` VALUES (2286, '1743', '    丝印染精加工', '174', 3, '    指对非自产的丝织品进行漂白、染色、轧光、起绒、缩水或印染等工序的加工。');
INSERT INTO `sys_industry_2002` VALUES (2287, '1751', '    棉及化纤制品制造', '175', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2288, '1752', '    毛制品制造', '175', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2289, '1753', '    麻制品制造', '175', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2290, '1754', '    丝制品制造', '175', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2291, '1755', '    绳、索、缆的制造', '175', 3, '    指用天然纤维和化学纤维制造绳、索具、缆绳、合股线的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2292, '1756', '    纺织带和帘子布制造', '175', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2293, '1757', '    无纺布制造', '175', 3, '    指以化学纤维为基本原料，经化学（或热熔）粘合而成的类似布的产品制造。因其不进行纺织，故又称为非织造布。');
INSERT INTO `sys_industry_2002` VALUES (2294, '1759', '    其他纺织制成品制造', '175', 3, '    指废旧纤维纺织品，特种纺织品以及其他未列明的纺织制成品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2295, '1761', '    棉、化纤针织品及编织品制造', '176', 3, '    指以棉及棉型化学纤维为主要原料，纯粹由手工织成或钩成，或由机器针织、钩针编织织物的制作活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2296, '1762', '    毛针织品及编织品制造', '176', 3, '     指以毛及毛型化学纤维为主要原料，纯粹由手工织成或钩成，或由机器针织、钩针编织织物的制作活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2297, '1763', '    丝针织品及编织品制造', '176', 3, '    指以丝及化纤长丝为主要原料，纯粹由手工织成或钩成，或由机器针织、钩针编织织物的制作活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2298, '1769', '    其他针织品及编织品制造', '176', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2299, '1810', '  纺织服装制造', '181', 3, '    指以纺织面料为主要原料，经裁剪后缝制各种男、女服装，以及儿童成衣的活动。包括非自产原料制作的服装，以及固定生产地点的服装制做。');
INSERT INTO `sys_industry_2002` VALUES (2300, '1820', '  纺织面料鞋的制造', '182', 3, '    指用各种纺织面料、木材、棕草等原料缝制、模压或编制各种鞋的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2301, '1830', '  制帽', '183', 3, '    指用各种纺织原料、皮革和毛皮原料，经剪裁、缝制或压制帽子的制作，以及针织或钩针编织成毛线帽的活动。');
INSERT INTO `sys_industry_2002` VALUES (2302, '1910', '  皮革鞣制加工', '191', 3, '    指动物生皮经脱毛、鞣制等物理和化学方法加工，再经涂饰和整理，制成具有不易腐烂、柔韧、透气等性能的皮革生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2303, '1921', '    皮鞋制造', '192', 3, '    指全部或大部分用皮革、人造革、合成革为面料，以橡胶、塑料或合成材料等为外底，按缝绱、胶粘、模压、注塑等工艺方法制作各种皮鞋的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2304, '1922', '    皮革服装制造', '192', 3, '    指全部或大部分用皮革、人造革、合成革为面料，制作各式服装的活动。');
INSERT INTO `sys_industry_2002` VALUES (2305, '1923', '    皮箱、包(袋)制造', '192', 3, '    指全部或大部分用皮革、人造革、合成革为材料，或者以塑料、纺织物为材料，制作各种用途的皮箱、皮包(袋)或其他材料的箱、包(袋)等的制作活动。');
INSERT INTO `sys_industry_2002` VALUES (2306, '1924', '         皮手套及皮装饰制品制造 ', '192', 3, '     指全部或大部分用皮革、人造革、合成革为材料制成的皮手套、皮带，以及皮领带等皮装饰制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2307, '1929', '    其他皮革制品制造', '192', 3, '    指全部或大部分用皮革、人造革、合成革为材料制成上述类别未列明的其他各种皮革制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2308, '1931', '    毛皮鞣制加工', '193', 3, '     指带毛动物生皮经鞣制等化学和物理方法处理后，保持其绒毛形态及特点的毛皮(又称裘皮)的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2309, '1932', '    毛皮服装加工', '193', 3, '     指用各种动物毛皮和人造毛皮为面料或里料，加工制作毛皮服装的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2310, '1939', '    其他毛皮制品加工', '193', 3, '     指用各种动物毛皮和人造毛皮为材料，加工制作上述类别未列明的其他各种用途毛皮制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2311, '1941', '    羽毛(绒)加工', '194', 3, '    指对鹅、鸭等禽类羽毛进行加工成标准毛的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2312, '1942', '    羽毛(绒)制品加工', '194', 3, '    指用加工过的羽毛(绒)作为填充物制作各种用途的羽绒制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2313, '2011', '    锯材加工', '201', 3, '    指以原木为原料，利用锯木机械或手工工具将原木纵向锯成具有一定断面尺寸（宽、厚度）的木材加工生产活动，用防腐剂和其他物质浸渍木料或对木料进行化学处理的加工活动，以及地板毛料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2314, '2012', '    木片加工', '201', 3, '    指利用森林采伐、造材、加工等剩余物和定向培育的木材，经削（刨）片机加工成一定规格的产品生产。');
INSERT INTO `sys_industry_2002` VALUES (2315, '2021', '    胶合板制造', '202', 3, '    指具有一定规格的原木经旋（刨）切成单板，再经干燥、涂胶、组坯、热压而成的符合国家标准及供需双方协定标准的产品生产。');
INSERT INTO `sys_industry_2002` VALUES (2316, '2022', '    纤维板制造', '202', 3, '    指用木材碎料（包括木片）、棉秆、甘蔗渣、芦苇等植物纤维作原料，经削片纤维分离，铺装成型，热压而成的产品生产。');
INSERT INTO `sys_industry_2002` VALUES (2317, '2023', '    刨花板制造', '202', 3, '     指用木材碎料（包括木片）和其他植物纤维作原料，制成刨花，经干燥、施胶，铺装成型，热压而成的产品生产。');
INSERT INTO `sys_industry_2002` VALUES (2318, '2029', '    其他人造板、材制造', '202', 3, '    指人造板二次加工装饰板及其他未列明的人造板材的制造。');
INSERT INTO `sys_industry_2002` VALUES (2319, '2031', '    建筑用木料及木材组件加工', '203', 3, '    指主要用于建筑施工工程的木质制品，如建筑施工用的大木工或其他支撑物，以及建筑木工的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2320, '2032', '    木容器制造', '203', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2321, '2039', '    软木制品及其他木制品制造', '203', 3, '    指天然软木除去表皮，经初加工后获得的结块软木及其制品，以及其他未列明的木质产品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2322, '2040', '    竹、藤、棕、草制品制造', '204', 3, '     指除木材以外，以 竹、藤、棕、草等天然植物为原料生产制品的活动。但不包括家具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2323, '2110', '  木质家具制造', '211', 3, '    指以天然木材和木质人造板为主要材料，配以其他辅料（如油漆、贴面材料、玻璃、五金配件等）制作各种家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2324, '2120', '  竹、藤家具制造', '212', 3, '    指以竹材和藤材为主要材料，配以其他辅料制作各种家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2325, '2130', '  金属家具制造', '213', 3, '    指支(框)架及主要部件以铸铁、钢材、钢板、钢管、合金等金属为主要材料，结合使用木、竹、塑等材料，配以人造革、尼龙布、泡沫塑料等其他辅料制作各种家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2326, '2140', '  塑料家具制造', '214', 3, '    指用塑料管、板、异型材加工或用塑料、玻璃钢（即增强塑料）直接在模具中成型的家具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2327, '2190', '  其他家具制造', '219', 3, '    指主要由弹性材料(如弹簧、蛇簧、拉簧等)和软质材料(如棕丝、棉花、乳胶海绵、泡沫塑料等)，辅以绷结材料(如绷绳、绷带、麻布等)和装饰面料及饰物(如棉、毛、化纤织物及牛皮、羊皮、人造革等)制成的各种软家具；以玻璃为主要材料，辅以木材或金属材料制成的各种玻璃家具，以及其他未列明的原材料制作各种家具的活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2328, '2210', '  纸浆制造', '221', 3, '    指经机械或化学方法加工纸浆的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2329, '2221', '    机制纸及纸板制造', '222', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2330, '2222', '    手工纸制造', '222', 3, '    指采用手工操作成型，制成纸的生产活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2331, '2223', '    加工纸制造', '222', 3, '    指对原纸及纸板进一步加工的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2332, '2231', '    纸和纸板容器的制造', '223', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2333, '2239', '    其他纸制品制造', '223', 3, '    指符合出售规格或包装要求的纸制品，以及其他未列明的纸制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2334, '2311', '    书、报、刊印刷', '231', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2335, '2312', '    本册印制', '231', 3, '    指由各种纸及纸板制作的，用于书写和其他用途的本册生产。');
INSERT INTO `sys_industry_2002` VALUES (2336, '2319', '    包装装潢及其他印刷', '231', 3, '    指根据一定的商品属性、形态，采用一定的包装材料，经过对商品包装的造型结构艺术和图案文字的设计与安排来装饰美化商品的印刷，以及其他印刷活动。');
INSERT INTO `sys_industry_2002` VALUES (2337, '2320', '  装订及其他印刷服务活动', '232', 3, '    指专门企业从事的装订、压印媒介制造等与印刷有关的服务活动。');
INSERT INTO `sys_industry_2002` VALUES (2338, '2330', '  记录媒介的复制', '233', 3, '    指将母带、母盘上的信息进行批量翻录的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2339, '2411', '    文具制造', '241', 3, '    指办公、学习等使用的各种文具的制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2340, '2412', '    笔的制造', '241', 3, '    指用于学习、办公或绘画等用途的各种笔制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2341, '2413', '    教学用模型及教具制造', '241', 3, '    指主要用于教学的各种专用模型、标本及教具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2342, '2414', '    墨水、墨汁制造', '241', 3, '    指书写、绘画等使用的墨水、墨汁及类似制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2343, '2419', '    其他文化用品制造', '241', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2344, '2421', '    球类制造', '242', 3, '    指各种皮制、胶制、革制的可充气的运动用球，以及其他材料制成的各种运动用硬球、软球等球类产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2345, '2422', '    体育器材及配件制造', '242', 3, '    指各项竞技比赛和训练用器材及用品，体育场馆设施及器件的生产。');
INSERT INTO `sys_industry_2002` VALUES (2346, '2423', '    训练健身器材制造', '242', 3, '    指供健身房、家庭或体育训练用的健身器材及运动物品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2347, '2424', '    运动防护用具制造', '242', 3, '    指用各种材质，为各项运动特制手套、鞋、帽和护具的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2348, '2429', '    其他体育用品制造', '242', 3, '    指钓鱼专用的各种用具及用品，以及上述未列明的体育用品制造。');
INSERT INTO `sys_industry_2002` VALUES (2349, '2431', '    中乐器制造', '243', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2350, '2432', '    西乐器制造', '243', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2351, '2433', '    电子乐器制造', '243', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2352, '2439', '    其他乐器及零件制造', '243', 3, '    指其他未列明的乐器、乐器零件及配套产品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2353, '2440', '  玩具制造', '244', 3, '     指以儿童为主要使用者，具备娱乐性、教育性和安全性三个基本特征的娱乐器具的制造。 ');
INSERT INTO `sys_industry_2002` VALUES (2354, '2451', '    露天游乐场所游乐设备制造', '245', 3, '    指主要安装在公园、游乐园、水上乐园、儿童乐园等露天游乐场所的电动及非电动游乐设备和游艺器材的制造。');
INSERT INTO `sys_industry_2002` VALUES (2355, '2452', '    游艺用品及室内游艺器材制造', '245', 3, '    指主要供室内、桌上等游艺及娱乐场所使用的游乐设备、游艺器材和游艺娱乐用品，以及主要安装在室内游乐场所的电子游乐设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2356, '2511', '    原油加工及石油制品制造', '251', 3, '    指从天然原油、人造原油中提炼液态或气态燃料，以及石油制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2357, '2512', '    人造原油生产', '251', 3, '    指从油母页岩中提炼原油的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2358, '2520', '  炼焦', '252', 3, '  指主要从硬煤和褐煤中生产焦炭、干馏炭及煤焦油或沥青等副产品的炼焦炉的操作活动。');
INSERT INTO `sys_industry_2002` VALUES (2359, '2530', '  核燃料加工', '253', 3, '  指从沥青铀矿或其他含铀矿石中提取铀、浓缩铀的生产，对铀金属的冶炼、加工的生产，以及其他放射性元素、同位素标记、核反应堆燃料元件的制造。还包括核废物处置活动。');
INSERT INTO `sys_industry_2002` VALUES (2360, '2611', '    无机酸制造', '261', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2361, '2612', '    无机碱制造', '261', 3, '    指烧碱、纯碱的生产。');
INSERT INTO `sys_industry_2002` VALUES (2362, '2613', '    无机盐制造', '261', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2363, '2614', '    有机化学原料制造', '261', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2364, '2619', '    其他基础化学原料制造', '261', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2365, '2621', '    氮肥制造', '262', 3, '     指矿物氮肥及用化学方法制成含有作物营养元素氮的化肥的生产。');
INSERT INTO `sys_industry_2002` VALUES (2366, '2622', '    磷肥制造', '262', 3, '    指以磷矿石为主要原料，用化学或物理方法制成含有作物营养元素磷的化肥的生产。');
INSERT INTO `sys_industry_2002` VALUES (2367, '2623', '    钾肥制造', '262', 3, '    指用天然钾盐矿经富集精制加工制成含有作物营养元素钾的化肥的生产。');
INSERT INTO `sys_industry_2002` VALUES (2368, '2624', '    复混肥料制造', '262', 3, '    指经过化学或物理方法加工制成的，含有两种以上作物所需主要营养元素（氮、磷、钾）的化肥的生产。包括通用型复混肥料和专用型复混肥料。');
INSERT INTO `sys_industry_2002` VALUES (2369, '2625', '    有机肥料及微生物肥料制造', '262', 3, '    指来源于动植物，经发酵或腐熟等化学处理后，适用于土壤并提供植物养分供给的，其主要成分为含氮物质的肥料制造。');
INSERT INTO `sys_industry_2002` VALUES (2370, '2629', '    其他肥料制造', '262', 3, '    指上述未列明的微量元素肥料及其他肥料的生产。');
INSERT INTO `sys_industry_2002` VALUES (2371, '2631', '         化学农药制造', '263', 3, '    指化学农药原药，以及经过机械粉碎、混合或稀释制成粉状、乳状和水状的化学农药制剂的生产。');
INSERT INTO `sys_industry_2002` VALUES (2372, '2632', '    生物化学农药及微生物农药制造', '263', 3, '    指由细菌、真菌、病毒和原生动物或基因修饰的微生物等自然产生，以及由植物提取的防治病、虫、草、鼠和其他有害生物的农药制剂生产。');
INSERT INTO `sys_industry_2002` VALUES (2373, '2641', '    涂料制造', '264', 3, '    指在天然树脂或合成树脂中加入颜料、溶剂和辅助材料，经加工后制成的覆盖材料的生产。                ');
INSERT INTO `sys_industry_2002` VALUES (2374, '2642', '    油墨及类似产品制造', '264', 3, '    指由颜料、联接料（植物油、矿物油、树脂、溶剂）和填充料经过混合、研磨调制而成用于印刷的有色胶浆状物质，以及用于计算机打印、复印机用墨的生产。 ');
INSERT INTO `sys_industry_2002` VALUES (2375, '2643', '    颜料制造', '264', 3, '     指用于陶瓷、搪瓷、玻璃等工业的无机颜料及类似材料的生产，以及油画、水粉画、广告等艺术用颜料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2376, '2644', '    染料制造', '264', 3, '    指有机合成、植物性或动物性色料，以及有机颜料的生产。');
INSERT INTO `sys_industry_2002` VALUES (2377, '2645', '        密封用填料及类似品制造', '264', 3, '    指用于建筑涂料、密封和漆工用的填充料，以及其他类似化学材料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2378, '2651', '    初级形态的塑料及合成树脂制造', '265', 3, '    也称初级塑料或原状塑料生产，包括通用塑料、工程塑料、功能高分子塑料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2379, '2652', '    合成橡胶制造', '265', 3, '    指人造橡胶或合成橡胶及高分子弹性体的生产。');
INSERT INTO `sys_industry_2002` VALUES (2380, '2653', '    合成纤维单(聚合)体的制造', '265', 3, '    指合成纤维单体和合成纤维聚合物的生产。');
INSERT INTO `sys_industry_2002` VALUES (2381, '2659', '    其他合成材料制造', '265', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2382, '2661', '    化学试剂和助剂制造', '266', 3, '    指各种化学试剂、催化剂及专用助剂的生产。');
INSERT INTO `sys_industry_2002` VALUES (2383, '2662', '    专项化学用品制造', '266', 3, '    指水处理化学品、造纸化学品、皮革化学品、油脂化学品、油田化学品、生物工程化学品、日化产品专用化学品、化学陶瓷纤维等特种纤维及高功能化工产品，以及其他各种用途的专项化学用品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2384, '2663', '    林产化学产品制造', '266', 3, '    指以林产品为原料，经过化学和物理加工方法生产产品的活动。');
INSERT INTO `sys_industry_2002` VALUES (2385, '2664', '    炸药及火工产品制造', '266', 3, '    指各种军用和民用炸药、雷管及类似的火工产品，节日用焰火制品及类似品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2386, '2665', '    信息化学品制造', '266', 3, '  指电影、照相、医用、幻灯及投影用感光材料、冲洗套药，磁、光记录材料，光纤维通讯用辅助材料，及其专用化学制剂的制造。');
INSERT INTO `sys_industry_2002` VALUES (2387, '2666', '        环境污染处理专用药剂材料制造', '266', 3, '    指对水污染、空气污染、固体废物等污染物处理所专用的化学药剂及材料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2388, '2667', '    动物胶制造', '266', 3, '    指以动物骨、皮为原料，经一系列工艺处理制成有一定透明度、粘度、纯度的胶产品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2389, '2669', '    其他专用化学产品制造\n    ', '266', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2390, '2671', '    肥皂及合成洗涤剂制造', '267', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2391, '2672', '    化妆品制造', '267', 3, '    指以涂抹、喷洒或其他类似方法，施于人体表面（如表皮、毛发、指甲、口唇等），起到清洁、保养、修饰、美化或消除不良气味，以及可对使用部位有缓和作用的产品制造。');
INSERT INTO `sys_industry_2002` VALUES (2392, '2673', '    口腔清洁用品制造', '267', 3, '    指用于口腔或牙齿清洁卫生制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2393, '2674', '    香料、香精制造', '267', 3, '    指具有香气和香味，用于调配香精的物质--香料的生产；以及以多种天然香料和合成香料为主要原料，并与其他辅料一起按合理的配方和工艺调配制得的具有一定香型的复杂混合物，主要用于各类加香产品中的香精的生产。');
INSERT INTO `sys_industry_2002` VALUES (2394, '2679', '    其他日用化学产品制造', '267', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2395, '2710', '  化学药品原药制造', '271', 3, '    指供进一步加工药品制剂所需的原药生产。');
INSERT INTO `sys_industry_2002` VALUES (2396, '2720', '  化学药品制剂制造', '272', 3, '    指直接用于人体疾病防治、诊断的化学药品制剂的制造。');
INSERT INTO `sys_industry_2002` VALUES (2397, '2730', '  中药饮片加工', '273', 3, '    指对采集的天然或人工种植、养殖的动物和植物及中草药进行加工、处理的活动。');
INSERT INTO `sys_industry_2002` VALUES (2398, '2740', '  中成药制造', '274', 3, '    指直接用于人体疾病防治的传统药的加工生产。');
INSERT INTO `sys_industry_2002` VALUES (2399, '2750', '  兽用药品制造', '275', 3, '   指用于动物疾病防治医药的制造。');
INSERT INTO `sys_industry_2002` VALUES (2400, '2760', '  生物、生化制品的制造', '276', 3, '   指利用生物技术生产生物化学药品、基因工程药物的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2401, '2770', '    卫生材料及医药用品制造', '277', 3, '    指卫生材料、外科敷料、药用包装材料以及其他内、外科用医药制品的制造。 ');
INSERT INTO `sys_industry_2002` VALUES (2402, '2811', '    化纤浆粕制造', '281', 3, '    指生产纺织用粘胶纤维的基本原料生产。');
INSERT INTO `sys_industry_2002` VALUES (2403, '2812', '    人造纤维（纤维素纤维）制造', '281', 3, '    指用化纤浆粕经机械加工生产纤维的活动。');
INSERT INTO `sys_industry_2002` VALUES (2404, '2821', '    锦纶纤维制造', '282', 3, '    也称聚酰胺纤维，指由尼龙66盐和聚己内酰胺为主要原料生产合成纤维的活动。');
INSERT INTO `sys_industry_2002` VALUES (2405, '2822', '    涤纶纤维制造', '282', 3, '    也称聚酯纤维，指以聚对苯二甲酸乙二醇酯（简称聚酯）为原料生产合成纤维的活动。');
INSERT INTO `sys_industry_2002` VALUES (2406, '2823', '    腈纶纤维制造', '282', 3, '    也称聚丙烯腈纤维，指以丙烯腈为主要原料（含丙烯腈85%以上）生产合成纤维的活动。');
INSERT INTO `sys_industry_2002` VALUES (2407, '2824', '    维纶纤维制造', '282', 3, '    也称聚乙烯醇纤维，指以聚乙烯醇为主要原料生产合成纤维的活动。');
INSERT INTO `sys_industry_2002` VALUES (2408, '2829', '    其他合成纤维制造', '282', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2409, '2911', '        车辆、飞机及工程机械轮胎制造', '291', 3, '    指充气轮胎外胎、内胎和实心轮胎的制造。');
INSERT INTO `sys_industry_2002` VALUES (2410, '2912', '    力车胎制造', '291', 3, '    指自行车、手推车和其他非机动车内胎、外胎的制造。');
INSERT INTO `sys_industry_2002` VALUES (2411, '2913', '    轮胎翻新加工', '291', 3, '    指将废轮胎翻新，以及轮胎零件的生产活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2412, '2920', '  橡胶板、管、带的制造', '292', 3, '    指用未硫化的、硫化的或硬质橡胶生产橡胶板状、片状、管状、带状、棒状和异型橡胶制品的活动，以及以橡胶为主要成分，用橡胶灌注、涂层、覆盖或层叠的纺织物、纱绳、钢丝（钢缆）等制作的传动带或输送带的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2413, '2930', '  橡胶零件制造', '293', 3, '    指各种用途的橡胶异形制品、橡胶零配件制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2414, '2940', '  再生橡胶制造', '294', 3, '    指用废橡胶生产再生橡胶的活动。');
INSERT INTO `sys_industry_2002` VALUES (2415, '2950', '    日用及医用橡胶制品制造', '295', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2416, '2960', '  橡胶靴鞋制造', '296', 3, '     指以橡胶作为鞋底、鞋帮的橡胶鞋及其橡胶鞋部件的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2417, '2990', '  其他橡胶制品制造', '299', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2418, '3010', '  塑料薄膜制造', '301', 3, '    指用于农业覆盖，工业、商业及日用包装薄膜的制造。');
INSERT INTO `sys_industry_2002` VALUES (2419, '3020', '  塑料板、管、型材的制造', '302', 3, '    指各种塑料板、管及管件、棒材、薄片等的生产，以及以聚氯乙烯为主要原料，经连续挤出成型的塑料异型材的生产。\n');
INSERT INTO `sys_industry_2002` VALUES (2420, '3030', '  塑料丝、绳及编织品的制造', '303', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2421, '3040', '  泡沫塑料制造', '304', 3, '    指以合成树脂为主要原料，经发泡成型工艺加工制成内部具有微孔的塑料制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2422, '3050', '  塑料人造革、合成革制造', '305', 3, '    指外观和手感似皮革，其透气、透湿性虽然略逊色于天然革，但它具有优异的物理、机械性能，如强度和耐磨性等，并可代替天然革使用的塑料人造革的生产，以及模拟天然人造革的组成和结构，正反面都与皮革十分相似，比普通人造革更近似天然革，并可代用天然革的塑料合成革的生产。');
INSERT INTO `sys_industry_2002` VALUES (2423, '3060', '  塑料包装箱及容器制造', '306', 3, '     指用吹塑或注塑工艺等制成的，可盛装各种物品或液体物质，以便于储存、运输等用途的塑料包装箱及塑料容器制品的生产。');
INSERT INTO `sys_industry_2002` VALUES (2424, '3070', '  塑料零件制造', '307', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2425, '3081', '    塑料鞋制造', '308', 3, '    指以聚氯乙烯、聚乙烯、聚氨酯和乙烯醋酸乙烯等树脂为原料生产发泡或不发泡的塑料鞋类制品的活动。');
INSERT INTO `sys_industry_2002` VALUES (2426, '3082', '    日用塑料杂品制造', '308', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2427, '3090', '  其他塑料制品制造', '309', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2428, '3111', '    水泥制造', '311', 3, '    指以水泥熟料加入适量石膏或一定混合材，经研磨设备(水泥磨)磨制到规定的细度制成水凝水泥的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2429, '3112', '    石灰和石膏制造', '311', 3, NULL);
