INSERT INTO `sys_industry_2002` VALUES (2432, '3123', '    石棉水泥制品制造', '312', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2433, '3124', '    轻质建筑材料制造', '312', 3, '    指石膏板、石膏制品及类似轻质建筑材料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2434, '3129', '    其他水泥制品制造', '312', 3, '    指玻璃纤维增强水泥制品，以及其他未列明的水泥制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2435, '3131', '    粘土砖瓦及建筑砌块制造', '313', 3, '    指用粘土和其他材料生产的砖、瓦及建筑砌块的活动。');
INSERT INTO `sys_industry_2002` VALUES (2436, '3132', '    建筑陶瓷制品制造', '313', 3, '    指用于建筑物的内、外墙及地面装饰或耐酸腐蚀的陶瓷材料（不论是否涂釉）的生产，以及水道、排水沟的陶瓷管道及配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2437, '3133', '    建筑用石加工', '313', 3, '    指用于建筑、筑路、墓地及其他用途的大理石板、花岗岩等石材的切割、成形和修饰活动。');
INSERT INTO `sys_industry_2002` VALUES (2438, '3134', '    防水建筑材料制造', '313', 3, '    指以沥青或类似材料为主要原料制造防水材料的活动。');
INSERT INTO `sys_industry_2002` VALUES (2439, '3135', '    隔热和隔音材料制造', '313', 3, '    指用于隔热、隔音、保温的岩石棉、矿渣棉、膨胀珍珠岩、膨胀蛭石等矿物绝缘材料及其制品的制造，但不包括石棉隔热、隔音材料的制造。');
INSERT INTO `sys_industry_2002` VALUES (2440, '3139', '    其他建筑材料制造', '313', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2441, '3141', '    平板玻璃制造', '314', 3, '    指用浮法、垂直引上法、压延法等生产平板玻璃原片的活动。');
INSERT INTO `sys_industry_2002` VALUES (2442, '3142', '    技术玻璃制品制造', '314', 3, '    指用于建筑、工业生产的技术玻璃制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2443, '3143', '    光学玻璃制造', '314', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2444, '3144', '        玻璃仪器制造', '314', 3, '    指实验室、医疗卫生用各种玻璃仪器和玻璃器皿的制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2445, '3145', '    日用玻璃制品及玻璃包装容器制造', '314', 3, '    指日用玻璃制品，以及主要用于产品包装的各种玻璃容器的制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2446, '3146', '    玻璃保温容器制造', '314', 3, '    指玻璃保温瓶和其他个人或家庭用玻璃保温容器的制造。\n');
