INSERT INTO `sys_industry_2002` VALUES (2449, '3149', '    其他玻璃制品制造', '314', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2450, '3151', '    卫生陶瓷制品制造', '315', 3, '    指卫生和清洁盥洗用的陶瓷用具的生产。');
INSERT INTO `sys_industry_2002` VALUES (2451, '3152', '    特种陶瓷制品制造', '315', 3, '    指专为工业、农业、实验室等领域的各种特定用途和要求，采用特殊生产工艺制造陶瓷制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2452, '3153', '    日用陶瓷制品制造', '315', 3, '     指以粘土、瓷石、长石、石英等为原料，经破碎、制泥、成型、烧炼等工艺制成，主要供日常生活用的各种瓷器、炻器、陶器等陶瓷制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2453, '3159', '    园林、陈设艺术及其他陶瓷制品制造', '315', 3, '    指以石英、长石、瓷土等为原料，经制胎、施釉、装饰、烧成等工艺制成的，具有艺术造型或花纹、图案等，主要供陈设、观赏或装饰用的纯艺术欣赏陶瓷制品和以欣赏为主的陶瓷陈列品、实用品的制造，以及其他未列明的陶瓷制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2454, '3161', '    石棉制品制造', '316', 3, '    指以石棉或其他矿物纤维素为基础，制造摩擦制品、石棉纺织制品、石棉橡胶制品、石棉保温隔热材料制品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2455, '3162', '    云母制品制造  ', '316', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2456, '3169', '    耐火陶瓷制品及其他耐火材料制造', '316', 3, '    指用硅质、粘土质、高铝质等石粉成形的陶瓷隔热制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2457, '3191', '    石墨及碳素制品制造', '319', 3, '    指以炭、石墨材料加工的特种石墨制品、碳素制品、异形制品，以及用树脂和各种有机物浸渍加工而成的炭素异形产品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2458, '3199', '    其他非金属矿物制品制造', '319', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2459, '3210', '  炼铁', '321', 3, '    指用高炉法、直接还原法、熔融还原法等，将铁从矿石等含铁化合物中还原出来的生产过程。');
INSERT INTO `sys_industry_2002` VALUES (2460, '3220', '  炼钢', '322', 3, '    指利用不同来源的氧（如空气、氧气）来氧化炉料（主要是生铁）所含杂质的金属提纯过程，称为炼钢活动。');
INSERT INTO `sys_industry_2002` VALUES (2461, '3230', '  钢压延加工', '323', 3, '    指通过塑性加工使连铸坯、钢锭产生塑性变形，制成具有一定形状尺寸的钢材产品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2462, '3240', '  铁合金冶炼', '324', 3, '    指通过热轧、冷加工、锻压和挤压等塑性加工使连铸坯、钢锭产生塑性变形，制成具有一定形状尺寸的钢材产品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2463, '3311', '    铜冶炼', '331', 3, '    指对铜精矿等矿山原料、废杂铜料进行熔炼、精炼、电解等提炼铜的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2464, '3312', '    铅锌冶炼', '331', 3, NULL);
