INSERT INTO `sys_industry_2002` VALUES (2467, '3315', '    锑冶炼', '331', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2468, '3316', '    铝冶炼', '331', 3, '     指对铝矿山原料通过冶炼、电解、铸型及对废杂铝料进行熔炼等提炼铝的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2469, '3317', '    镁冶炼', '331', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2470, '3319', '    其他常用有色金属冶炼', '331', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2471, '3321', '    金冶炼', '332', 3, '    指用金精（块）矿、阳极泥（冶炼其他有色金属时回收的阳极泥含金）、废杂金提炼黄金的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2472, '3322', '    银冶炼', '332', 3, '     指用银精（块）矿、阳极泥（冶炼其他有色金属时回收的阳极泥含银）、废杂银提炼白银的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2473, '3329', '    其他贵金属冶炼', '332', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2474, '3331', '    钨钼冶炼', '333', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2475, '3332', '    稀土金属冶炼', '333', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2476, '3339', '        其他稀有金属冶炼', '333', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2477, '3340', '  有色金属合金制造', '334', 3, '    指以有色金属为基体，加入一种或几种其他元素所构成的合金生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2478, '3351', '    常用有色金属压延加工', '335', 3, '    指铜及铜合金、铝及铝合金等常用有色金属及合金的压延加工生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2479, '3352', '    贵金属压延加工', '335', 3, '    指对金、银及铂族等贵金属，进行轧制、拉制或挤压加工的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2480, '3353', '    稀有稀土金属压延加工', '335', 3, '    指对钨、钼、钽等稀有金属材，以及半导体材料的加工。');
INSERT INTO `sys_industry_2002` VALUES (2481, '3411', '    金属结构制造', '341', 3, '    指以铁、钢或铝等金属为主要材料，制造金属构件、金属构件零件、建筑用钢制品及类似品的生产活动。这些制品可以运输，并便于装配、安装或竖立（如由建筑企业在建筑工地进行）。');
INSERT INTO `sys_industry_2002` VALUES (2482, '3412', '    金属门窗制造', '341', 3, '    指用金属材料（铝合金或其他金属）制作建筑物用门窗及类似品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2483, '3421', '    切削工具制造', '342', 3, '    指手工或机床用可互换的切削工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2484, '3422', '    手工具制造', '342', 3, '    指人们在生产和日常生活中，进行装配、安装、维修时使用的手工工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2485, '3423', '    农用及园林用金属工具制造', '342', 3, '    指主要用于农牧业生产的小农具，园艺或林业作业用金属工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2486, '3424', '    刀剪及类似日用金属工具制造', '342', 3, '    指日常生活用刀剪、指甲钳等类似金属工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2487, '3429', '    其他金属工具制造', '342', 3, '    指上述类别未包括的用于各种用途的金属工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2488, '3431', '    集装箱制造', '343', 3, '     指专门设计，可长期反复使用，不用换箱内货物，便可从一种运输方式转移到另一种运输方式的放置货物的钢质箱体（其容积大于1立方米）的生产和修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2489, '3432', '    金属压力容器制造', '343', 3, '    指用于存装压缩气体、液化气体及其他具有一定压力的液体物质的金属容器（不论其是否配有顶盖、塞子，或衬有除铁、钢、铝以外的材料）的制造。');
INSERT INTO `sys_industry_2002` VALUES (2490, '3433', '    金属包装容器制造', '343', 3, '    指主要为商品运输或包装而制作的金属包装容器及附件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2491, '3440', '  金属丝绳及其制品的制造', '344', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2492, '3451', '    建筑、家具用金属配件制造', '345', 3, '    指用于建筑物、家具、交通工具或其他场所和用具的金属装置、锁及其金属配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2493, '3452', '    建筑装饰及水暖管道零件制造', '345', 3, '    指用于建筑方面的金属装饰材料，以及建筑工程对中性介质（如水、油、蒸汽、空气、煤气等没有腐蚀性的气体和液体物质）在低压下进行工作的设备和管道上所使用的金属附件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2494, '3453', '    安全、消防用金属制品制造', '345', 3, '    指安全、消防用金属保险柜、保险箱、消防梯等金属制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2495, '3459', '    其他建筑、安全用金属制品制造', '345', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2496, '3460', '  金属表面处理及热处理加工', '346', 3, '    指对外来的金属物件表面进行的电镀、镀层、抛光、喷涂、着色等专业性作业加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2497, '3471', '    工业生产配套用搪瓷制品制造', '347', 3, '     指用于工业生产，专为工业生产设备、工业产品及家电配套的各种搪瓷制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2498, '3472', '    搪瓷卫生洁具制造', '347', 3, '    指卫生用和清洁盥洗用搪瓷用具的生产。');
INSERT INTO `sys_industry_2002` VALUES (2499, '3479', '    搪瓷日用品及其他搪瓷制品制造', '347', 3, '    指金属薄板经过成型、搪烧制成的日用品及其他搪瓷制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2500, '3481', '    金属制厨房调理及卫生器具制造', '348', 3, '    指厨房调理、卫生用和清洁盥洗用的各种金属用具的生产。');
INSERT INTO `sys_industry_2002` VALUES (2501, '3482', '    金属制厨用器皿及餐具制造', '348', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2502, '3489', '    其他日用金属制品制造', '348', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2503, '3491', '    铸币及贵金属制实验室用品制造', '349', 3, '    指用金属制成的各种金属硬币，以及贵金属制实验室用品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2504, '3499', '    其他未列明的金属制品制造', '349', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2505, '3511', '    锅炉及辅助设备制造', '351', 3, '    指各种蒸汽锅炉、汽化锅炉，以及除同位素分离器以外的各种核反应堆的制造。');
INSERT INTO `sys_industry_2002` VALUES (2506, '3512', '        内燃机及配件制造', '351', 3, '     指用于移动或固定用途的往复式、旋转式、火花点火式或压燃式内燃机及配件的制造。但不包括飞机、汽车和摩托车发动机的制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2507, '3513', '    汽轮机及辅机制造', '351', 3, '    指汽轮机和燃气轮机（蒸汽涡轮机）的制造。');
INSERT INTO `sys_industry_2002` VALUES (2508, '3514', '    水轮机及辅机制造', '351', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2509, '3519', '    其他原动机制造', '351', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2510, '3521', '    金属切削机床制造', '352', 3, '    指用于加工金属的各种切削加工机床的制造。');
INSERT INTO `sys_industry_2002` VALUES (2511, '3522', '    金属成形机床制造', '352', 3, '    指以锻压、锤击和模压方式加工金属的机床，或以弯曲、折叠、矫直、剪切、冲压、开槽、拉丝等方式加工金属的机床制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2512, '3523', '    铸造机械制造', '352', 3, '    指金属铸造用机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2513, '3524', '    金属切割及焊接设备制造', '352', 3, '    指将电能及其他形式的能量转换为切割、焊接能量对金属进行切割、焊接的设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2514, '3525', '    机床附件制造', '352', 3, '    指扩大机床加工性能和使用范围的附属装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2515, '3529', '    其他金属加工机械制造', '352', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2516, '3530', '  起重运输设备制造', '353', 3, '    指在工厂、仓库、码头、站台及其他地方装卸材料、货物或人的机械设备，可连续运行或间歇运行的机械，固定式及移动式机械，以及安装在带轮底盘上的机械制造。');
INSERT INTO `sys_industry_2002` VALUES (2517, '3541', '    泵及真空设备制造', '354', 3, '    指用以输送各种液体、液固混合体、液气混合体及其增压、循环、真空等用途的设备制造。');
INSERT INTO `sys_industry_2002` VALUES (2518, '3542', '    气体压缩机械制造', '354', 3, '    指对气体进行压缩，使其压力提高到350kPa以上的压缩机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2519, '3543', '    阀门和旋塞的制造', '354', 3, '    指通过改变其流道面积的大小，用以控制流体流量、压力和流向的装置制造。');
INSERT INTO `sys_industry_2002` VALUES (2520, '3544', '    液压和气压动力机械及元件制造', '354', 3, '    指以液体为工作介质，靠液体静压力来传送能量的装置制造。');
INSERT INTO `sys_industry_2002` VALUES (2521, '3551', '    轴承制造', '355', 3, '    指各种轴承及轴承零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2522, '3552', '    齿轮、传动和驱动部件制造', '355', 3, '    指用于传递动力，实现机械输送和提升重物的基础件，铰接式链条等机械传动装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2523, '3560', '  烘炉、熔炉及电炉制造', '356', 3, '    指使用液体燃料、粉状固体燃料（焚化炉）或气体燃料，进行煅烧、熔化或其他热处理用的非电力熔炉、窑炉和烘炉等燃烧器的制造，以及工业或实验室用电炉及零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2524, '3571', '    风机、风扇制造', '357', 3, '    指用来输送各种气体，以及气体增压、循环、通风换气、排尘等设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2525, '3572', '        气体、液体分离及纯净设备制造', '357', 3, '    指气体和液体的提纯、分离、液化、过滤、净化等设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2526, '3573', '    制冷、空调设备制造', '357', 3, '    指用于专业生产、商业经营等方面的制冷设备和空调设备的制造。但不包括家用空调设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2527, '3574', '    风动和电动工具制造', '357', 3, '    指带有电动机、非电力发动机或风动装置的手工操作加工工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2528, '3575', '    喷枪及类似器具制造 ', '357', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2529, '3576', '    包装专用设备制造', '357', 3, '    指对瓶、桶、箱、袋或其他容器的洗涤、干燥、装填、密封和贴标签等专用包装机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2530, '3577', '    衡器制造', '357', 3, '    指用来测定物质重量的各种机械的、电子的或机电结合的装置或设备的生产。');
INSERT INTO `sys_industry_2002` VALUES (2531, '3579', '    其他通用设备制造', '357', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2532, '3581', '    金属密封件制造', '358', 3, '    指以金属为原料制作密封件的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2533, '3582', '    紧固件、弹簧制造', '358', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2534, '3583', '    机械零部件加工及设备修理', '358', 3, '    指对专用和通用机械零部件的加工及修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2535, '3589', '    其他通用零部件制造', '358', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2536, '3591', '        钢铁铸件制造', '359', 3, '    指钢铁金属铸造的各种成品或半成品的制造活动。');
INSERT INTO `sys_industry_2002` VALUES (2537, '3592', '    锻件及粉末冶金制品制造', '359', 3, '    指通过对金属坯料进行锻造变形而得到的工件或毛坯，或者将金属粉末和与非金属粉末的混合物通过压制变形、烘焙制作制品和材料的活动。');
INSERT INTO `sys_industry_2002` VALUES (2538, '3611', '    采矿、采石设备制造', '361', 3, '    指地下或露天，用于对金属、煤炭、石油、化工等各种矿石或建筑用石的开采设备制造，以及矿石筛选、分类、分离、洗选、轧碎或类似加工工艺使用的专用机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2539, '3612', '    石油钻采专用设备制造', '361', 3, '    指对陆地和海洋的石油、天然气等专用开采设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2540, '3613', '    建筑工程用机械制造', '361', 3, '    指建筑施工及市政公共工程用机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2541, '3614', '        建筑材料生产专用机械制造', '361', 3, '    指生产水泥、水泥制品、玻璃及玻璃纤维、建筑陶瓷、砖瓦等建筑材料所使用的各种生产、搅拌成型机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2542, '3615', '    冶金专用设备制造    ', '361', 3, '    指金属冶炼、轧制、铸造等生产专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2543, '3621', '    炼油、化工生产专用设备制造', '362', 3, '    指炼油、化学工业生产专用设备的制造，但不包括包装机械等通用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2544, '3622', '    橡胶加工专用设备制造', '362', 3, '    指加工橡胶，或以橡胶为材料生产橡胶制品的专用机械制造。');
INSERT INTO `sys_industry_2002` VALUES (2545, '3623', '    塑料加工专用设备制造', '362', 3, '    指塑料加工工业中所使用的各类专用机械和装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2546, '3624', '    木材加工机械制造        ', '362', 3, '    指加工木材、木质板材及木制品的生产专用机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2547, '3625', '    模具制造', '362', 3, '    指金属铸造用模具、矿物材料用模具、橡胶或塑料用模具及其他用途的模具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2548, '3629', '    其他非金属加工专用设备制造', '362', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2549, '3631', '    食品、饮料、烟草工业专用设备制造', '363', 3, '     指主要用于食品、饮料酒及饮料生产、烟草制品加工等专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2550, '3632', '    农副食品加工专用设备制造', '363', 3, '    指对谷物、干豆类等农作物的筛选、碾磨、储存等专用机械，糖料和油料作物加工机械，畜禽屠宰、水产品加工及盐加工机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2551, '3633', '    饲料生产专用设备制造', '363', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2552, '3641', '    制浆和造纸专用设备制造', '364', 3, '    指在制浆、造纸、纸加工及纸制品的生产过程中所用的各类机械和设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2553, '3642', '    印刷专用设备制造', '364', 3, '    指使用印刷或其他方式将图文信息转移到承印物上的专用生产设备的制造活动。');
INSERT INTO `sys_industry_2002` VALUES (2554, '3643', '    日用化工专用设备制造', '364', 3, '    指日用化学工业产品，如洗涤用品、口腔清洁用品、化妆品、香精、香料、动物胶、感光材料及其他日用化学制品专用生产设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2555, '3644', '    制药专用设备制造', '364', 3, '    指化学原料药和药剂、中药饮片及中成药专用生产设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2556, '3645', '        照明器具生产专用设备制造', '364', 3, '    指用于生产各种电灯泡、荧光灯管等电光源和各种照明器具产品专用生产设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2557, '3646', '    玻璃、陶瓷和搪瓷制品生产专用设备制造', '364', 3, '    指用于生产加工玻璃制品、玻璃器皿的专用机械，陶瓷器等类似产品的加工机床和生产专用机械，以及搪瓷制品生产设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2558, '3649', '    其他日用品生产专用设备制造', '364', 3, '    指上述未列明的日用品、工艺美术品的生产专用机械设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2559, '3651', '    纺织专用设备制造 ', '365', 3, '    指纺织纤维预处理、纺纱、织造和针织机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2560, '3652', '    皮革、毛皮及其制品加工专用设备制造', '365', 3, '    指在制革、毛皮鞣制及其制品的加工生产过程中所使用的各种专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2561, '3653', '    缝纫机械制造', '365', 3, '    指用于服装、鞋帽制作的专用缝纫机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2562, '3659', '    其他服装加工专用设备制造', '365', 3, '    指除缝纫机以外，生产加工各种面料服装、鞋帽，以及洗衣店所使用的类似机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2563, '3661', '    电工机械专用设备制造', '366', 3, '  指电机、电线、电缆等电站、电工专用机械及器材的生产设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2564, '3662', '    电子工业专用设备制造', '366', 3, '    指生产半导体器件、集成电路、电子元件、电真空器件，以及电子设备整机装配专用设备的制造。');
