INSERT INTO `sys_industry_2002` VALUES (2568, '3672', '    机械化农业及园艺机具制造', '367', 3, '    指用于土壤处理，作物种植或施肥，种植物收割的农业、园艺或其他机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2569, '3673', '    营林及木竹采伐机械制造', '367', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2570, '3674', '    畜牧机械制造', '367', 3, '    指草原建设、管理，畜禽养殖及畜禽产品采集等专用机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2571, '3675', '    渔业机械制造', '367', 3, '    指渔业养殖、渔业捕捞等专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2572, '3676', '    农林牧渔机械配件制造', '367', 3, '    指拖拉机配件和其他农林牧渔机械配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2573, '3679', '    其他农林牧渔业机械制造及机械修理', '367', 3, '    指用于农产品初加工机械，以及其他未列明的农林牧渔业机械的制造及机械修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2574, '3681', '    医疗诊断、监护及治疗设备制造', '368', 3, '    指用于内科、外科、眼科、牙科、妇产科、中医等医疗专用及兽医用诊断、监护、治疗等方面的设备制造与修理。');
INSERT INTO `sys_industry_2002` VALUES (2575, '3682', '        口腔科用设备及器具制造', '368', 3, '    指用于口腔治疗、修补的设备及器械的制造与修理。');
INSERT INTO `sys_industry_2002` VALUES (2576, '3683', '    实验室及医用消毒设备和器具的制造', '368', 3, '    指实验室或医疗用的消毒、灭菌设备及器具的制造与修理。');
INSERT INTO `sys_industry_2002` VALUES (2577, '3684', '    医疗、外科及兽医用器械制造', '368', 3, '    指各种手术室、急救室、诊疗室等医疗专用及兽医用的手术器械、医疗诊断用品和医疗用具的制造与修理。');
INSERT INTO `sys_industry_2002` VALUES (2578, '3685', '    机械治疗及病房护理设备制造', '368', 3, '    指各种治疗设备、病房护理及康复专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2579, '3686', '        假肢、人工器官及植（介）入器械制造', '368', 3, '    指外科、牙科等医疗专用及兽医用的假肢、人工器官、植入器械，矫形器具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2580, '3689', '    其他医疗设备及器械制造', '368', 3, '    指外科、牙科等医疗专用及兽医用的家具器械，以及其他未列明的医疗设备及器械的制造与修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2581, '3691', '    环境污染防治专用设备制造', '369', 3, '    指环境污染防治、废旧物品加工，以及工业材料回收专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2582, '3692', '    地质勘查专用设备制造', '369', 3, '    指地质勘查（勘探）专用设备的制造，不包括通用钻采、挖掘机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2583, '3693', '    邮政专用机械及器材制造', '369', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2584, '3694', '    商业、饮食、服务业专用设备制造', '369', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2585, '3695', '    社会公共安全设备及器材制造', '369', 3, '    指公安、消防、安全等社会公共安全设备及器材的制造、加工和修理活动。\n   ');
INSERT INTO `sys_industry_2002` VALUES (2586, '3696', '    交通安全及管制专用设备制造', '369', 3, '     指除铁路以外的各种道路、内河航道、停车场、港口、机场等使用的讯号、安全或交通控制设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2587, '3697', '    水资源专用机械制造', '369', 3, '    指水利工程管理、节水工程及水的生产、供应专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2588, '3699', '    其他专用设备制造', '369', 3, '     指上述类别中未列明的其他专用设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2589, '3711', '    铁路机车车辆及动车组制造', '371', 3, '    指以外来电源或以蓄电池驱动的，或以压燃式发动机及其他方式驱动的，能够牵引铁路车辆的动力机车、铁路动车组的制造，以及用于运送旅客和用以装运货物的客车、货车及其他铁路专用车辆的制造。');
INSERT INTO `sys_industry_2002` VALUES (2590, '3712', '    工矿有轨专用车辆制造', '371', 3, '    指专门在企业内部和矿山使用的非铁路干线的轨道式运输车辆的制造。');
INSERT INTO `sys_industry_2002` VALUES (2591, '3713', '    铁路机车车辆配件制造', '371', 3, '    指铁道或有轨机车及其拖拽车辆的专用零配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2592, '3714', '    铁路专用设备及器材、配件制造', '371', 3, '    指铁路信号、安全或交通控制设备，以及其他铁路专用设备及器材、配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2593, '3719', '        其他铁路设备制造及设备修理', '371', 3, '     指独立的铁路机车修理企业对铁路各种运输车辆设备和器材的修理活动，以及其他未列明的铁路运输设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2594, '3721', '    汽车整车制造', '372', 3, '    指由动力装置驱动，具有四个以上车轮的非轨道、无架线的车辆，并主要用于载送人员和（或）货物，牵引输送人员和（或）货物的车辆制造。还包括汽车发动机的制造。');
INSERT INTO `sys_industry_2002` VALUES (2595, '3722', '    改装汽车制造', '372', 3, '     指利用外购汽车底盘改装各类汽车的制造活动。');
INSERT INTO `sys_industry_2002` VALUES (2596, '3723', '    电车制造', '372', 3, '    指以电作为动力，以屏板或可控硅方式控制的城市内交通工具和专用交通工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2597, '3724', '    汽车车身、挂车的制造', '372', 3, '    指其设计和技术特性需由汽车牵引，才能正常行驶的一种无动力的道路车辆的制造。');
INSERT INTO `sys_industry_2002` VALUES (2598, '3725', '    汽车零部件及配件制造', '372', 3, '    指机动车辆及其车身的各种零配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2599, '3726', '    汽车修理', '372', 3, '    指专业汽车修理企业进行的修理活动，但不包括非汽车修理厂（即规模较小的路边修理服务部）的修理和维护。');
INSERT INTO `sys_industry_2002` VALUES (2600, '3731', '    摩托车整车制造', '373', 3, '    指不论是否装有边斗的摩托车制造。');
INSERT INTO `sys_industry_2002` VALUES (2601, '3732', '    摩托车零部件及配件制造', '373', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2602, '3741', '    脚踏自行车及残疾人座车制造', '374', 3, '    指未装马达，主要以脚蹬驱动，装有一个或多个轮子的脚踏车辆、残疾人座车及其零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2603, '3742', '    助动自行车制造', '374', 3, '     指主要以蓄电池作为辅助能源，具有两个车轮，能实现人力骑行、电动或电动助力功能的特种自行车及其零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2604, '3751', '    金属船舶制造', '375', 3, '    指以钢质、铝质等各种金属为主要材料，为民用或军事部门建造的远洋、近海或内陆河湖的金属船舶的制造活动。');
INSERT INTO `sys_industry_2002` VALUES (2605, '3752', '    非金属船舶制造', '375', 3, '    指以各种木材、水泥、玻璃钢等非金属材料，为民用或军事部门建造船舶的活动。');
INSERT INTO `sys_industry_2002` VALUES (2606, '3753', '    娱乐船和运动船的建造和修理', '375', 3, '    指游艇和用于娱乐或运动的其他船只的建造与修理。');
INSERT INTO `sys_industry_2002` VALUES (2607, '3754', '    船用配套设备制造', '375', 3, '    指船用主机、辅机设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2608, '3755', '        船舶修理及拆船', '375', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2609, '3759', '        航标器材及其他浮动装置的制造', '375', 3, '    指用于航标的各种器材、浮式装置，以及不以航行为主的船只的制造。');
INSERT INTO `sys_industry_2002` VALUES (2610, '3761', '    飞机制造及修理', '376', 3, '  指在大气同温层以内飞行的用于运货或载客，用于国防，以及用于体育运动或其他用途的各种飞机及其零件的制造和修理活动。\n');
INSERT INTO `sys_industry_2002` VALUES (2611, '3762', '    航天器制造', '376', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2612, '3769', '    其他飞行器制造', '376', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2613, '3791', '    潜水及水下救捞装备制造', '379', 3, '    指潜水装置及水下作业、救捞装备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2614, '3792', '    交通管理用金属标志及设施制造', '379', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2615, '3799', '    其他交通运输设备制造', '379', 3, '    指手推车辆、牲畜牵引车辆的制造，以及上述未列明的交通运输设备的制造和车辆修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2616, '3911', '    发电机及发电机组制造', '391', 3, '    指发电机及其辅助装置、发电成套设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2617, '3912', '    电动机制造', '391', 3, '    指交流或直流电动机及零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2618, '3919', '    微电机及其他电机制造', '391', 3, '    指自动化系统中一种主要用于传递和交换信号等方面的元件，即控制微电机的制造，以及其他未列明的电机制造。');
INSERT INTO `sys_industry_2002` VALUES (2619, '3921', '    变压器、整流器和电感器制造', '392', 3, '    指变压器、静止式变流器等电力电子设备和互感器的制造。');
INSERT INTO `sys_industry_2002` VALUES (2620, '3922', '        电容器及其配套设备制造', '392', 3, '    指电力电容器、其他电容器及其配套装置和电容器零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2621, '3923', '    配电开关控制设备制造', '392', 3, '     指用于电压超过1000V的，诸如一般在配电系统中使用的接通及断开或保护电路的电器，以及用于电压不超过1000V的如在住房、工业设备或家用电器中使用的配电开关控制设备及其零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2622, '3924', '    电力电子元器件制造', '392', 3, '    指用于电能变换和控制（从而实现运动控制）的电子产品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2623, '3929', '    其他输配电及控制设备制造', '392', 3, '    指开关设备和控制设备内部的元器件之间，以及与外部电路之间的电连接所需用的器件和配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2624, '3931', '    电线电缆制造', '393', 3, '    指在电力输配、电能传送，声音、文字、图像等信息传播，以及照明等各方面所使用的电线电缆的制造。');
INSERT INTO `sys_industry_2002` VALUES (2625, '3932', '        光纤、光缆制造', '393', 3, '    指将电的信号变成光的信号，进行声音、文字、图像等信息传输的光缆.。');
INSERT INTO `sys_industry_2002` VALUES (2626, '3933', '    绝缘制品制造', '393', 3, '    指电气绝缘子、电机或电气设备用的绝缘零件，以及带有绝缘材料的金属制电导管及接头的制造。但不包括玻璃、陶瓷绝缘体和绝缘漆制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2627, '3939', '    其他电工器材制造', '393', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2628, '3940', '  电池制造', '394', 3, '    指以正极活性材料、负极活性材料，配合电介质，以密封式结构制成的，并具有一定公称电压和额定容量的化学电源的制造。包括一次性、不可充电和二次可充电，重复使用的干电池、蓄电池，以及利用氢与氧的合成转换成电能的装置，即燃料电池和利用太阳光转换成电能的太阳能电池的制造。');
INSERT INTO `sys_industry_2002` VALUES (2629, '3951', '    家用制冷电器具制造', '395', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2630, '3952', '    家用空气调节器制造', '395', 3, '    指使用交流电源(制冷量14000W及以下)，调节室内温度、湿度、气流速度和空气洁净度的房间空气调节器的制造。');
INSERT INTO `sys_industry_2002` VALUES (2631, '3953', '    家用通风电器具制造', '395', 3, '    指由单相交流电动机驱动扇叶旋转，产生强制气流，以改善人体与周围空气间的热交换条件的电器制造。');
INSERT INTO `sys_industry_2002` VALUES (2632, '3954', '    家用厨房电器具制造', '395', 3, '    指家庭厨房用的电热蒸煮器具、电热烘烤器具、电热水和饮料加热器具、电热煎炒器具、家用电灶、家用食品加工电器具、家用厨房电清洁器具等电器具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2633, '3955', '    家用清洁卫生电器具制造', '395', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2634, '3956', '    家用美容、保健电器具制造', '395', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2635, '3957', '    家用电力器具专用配件制造', '395', 3, '    指家用电力器具专用配件的制造，不包括通用零部件。');
INSERT INTO `sys_industry_2002` VALUES (2636, '3959', '    其他家用电力器具制造', '395', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2637, '3961', '    燃气、太阳能及类似能源的器具制造', '396', 3, '    指以液化气、天然气、人工煤气、沼气或太阳能作燃料，以马口铁、搪瓷、不锈钢等为材料加工制成的家用器具的生产。');
INSERT INTO `sys_industry_2002` VALUES (2638, '3969', '    其他非电力家用器具制造', '396', 3, NULL);
INSERT INTO `sys_industry_2002` VALUES (2639, '3971', '    电光源制造', '397', 3, '     电光源也称灯泡或电灯，本类是指将电能转变为光的器件的制造。目前按发光原理可分为白炽灯（指因电流通过使钨丝白炽而发光的灯）和气体放电灯（指电流通过灯两端的电极形成气体放电而产生光的灯）。');
INSERT INTO `sys_industry_2002` VALUES (2640, '3972', '    照明灯具制造', '397', 3, '     指由起支撑、固定反射和保护作用的部件及联结光源所必须的电路辅助装置组合而成，将一个或多个光源发出的光进行控制分配或反射装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2641, '3979', '    灯用电器附件及其他照明器具制造', '397', 3, '     指灯用电器附件，以及为各种灯泡配套用的灯座及其他照明器具的制造。   ');
INSERT INTO `sys_industry_2002` VALUES (2642, '3991', '    车辆专用照明及电气信号设备装置制造', '399', 3, '    指交通运输工具（如机动车、船舶、铁道车辆等）专用电力照明及信号装置，各种电气音响或视觉报警、警告、指示装置，以及其他电气声像信号装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2643, '3999', '    其他未列明的电气机械制造', '399', NULL, '    指上述未列明的电气机械及器材的制造，以及电气机械的修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2644, '4011', '    通信传输设备制造', '401', NULL, '     指有线或无线通信传输设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2645, '4012', '        通信交换设备制造', '401', NULL, '     指实现电路（信息）交换或接口功能设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2646, '4013', '    通信终端设备制造', '401', NULL, '     指有线电话机、可视电话、传真设备等各种有线通信终端接收设备的制造，但不包括无线电话机的制造。');
INSERT INTO `sys_industry_2002` VALUES (2647, '4014', '    移动通信及终端设备制造', '401', NULL, '    指移动通信设备及无线电话机（手机）等终端设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2648, '4019', '    其他通信设备制造', '401', NULL, '    指其他通信设备的制造，以及对通信设备的修理。');
INSERT INTO `sys_industry_2002` VALUES (2649, '4020', '  雷达及配套设备制造', '402', NULL, '    指雷达整机及雷达配套产品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2650, '4031', '    广播电视节目制作及发射设备制造', '403', NULL, '    指广播电视节目制作、发射设备及器材的制造。');
INSERT INTO `sys_industry_2002` VALUES (2651, '4032', '    广播电视接收设备及器材制造', '403', NULL, '    指专业广播电视接收设备、专业用录音录像重放、音响设备及其他配套的广播电视设备的制造，但不包括家用广播电视接收设备及装置。');
INSERT INTO `sys_industry_2002` VALUES (2652, '4039', '    应用电视设备及其他广播电视设备制造', '403', NULL, '    指应用电视设备、其他电视设备和器材的制造，以及对广播电视专用设备的修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2653, '4041', '    电子计算机整机制造', '404', NULL, '    指可进行算术运算或逻辑运算，包括中央处理机，并配有输入、输出装置和存储功能及其他外围设备的成套数字系统装置的制造，还包括来件组装电子计算机的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2654, '4042', '    计算机网络设备制造', '404', NULL, '    指建立某一计算机系统网络所需各种相关设备或装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2655, '4043', '    电子计算机外部设备制造', '404', NULL, '    指电子计算机外部设备及附件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2656, '4051', '    电子真空器件制造', '405', NULL, '    指电子热离子管、冷阴极管或光电阴极管及其他真空电子器件，以及电子管零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2657, '4052', '    半导体分立器件制造', '405', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2658, '4053', '    集成电路制造', '405', NULL, '    指单片集成电路、混合式集成电路和组装好的电子模压组件、微型组件或类似组件的制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2659, '4059', '    光电子器件及其他电子器件制造', '405', NULL, '    指光电子器件、显示器件和组件，以及其他未列明的电子器件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2660, '4061', '    电子元件及组件制造\n\n', '406', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2661, '4062', '    印制电路板制造', '406', NULL, '    指在绝缘板上通过常规或非常规的印刷工艺，使导电元件、触点或电感器件、电阻器和电容器等其他印刷元件组成的电路及专用元件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2662, '4071', '    家用影视设备制造', '407', NULL, '    指家用电视机、录像机、摄像机、激光视盘机等影视设备整机及零部件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2663, '4072', '    家用音响设备制造', '407', NULL, '     指家用无线电收音机、收录音机、唱机等家用音响设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2664, '4090', '  其他电子设备制造', '409', NULL, '    指电子（气）物理设备及其他未列明的电子设备的制造。');
INSERT INTO `sys_industry_2002` VALUES (2665, '4111', '    工业自动控制系统装置制造', '411', NULL, '   指用于工业产品制造或加工过程中，连续自动测量，控制材料或产品的温度、压力、粘度等变量的工业控制用计算机系统、仪表和装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2666, '4112', '    电工仪器仪表制造', '411', NULL, '    指测量或检验电压、电流、电阻或功率的通用仪器装置的制造，但不包括发电或供电过程中计量仪表的制造。');
INSERT INTO `sys_industry_2002` VALUES (2667, '4113', '    绘图、计算及测量仪器制造', '411', NULL, '    指供设计、制图、 绘图、计算、测量，以及学习或办公、教学等使用的测量和绘图用具、器具、精密天平及量仪的制造。');
INSERT INTO `sys_industry_2002` VALUES (2668, '4114', '        实验分析仪器制造', '411', NULL, '    指实验、检验、分析、测量液体或气体的流量、比重、压力、温度、湿度、粘度的仪器制造。');
INSERT INTO `sys_industry_2002` VALUES (2669, '4115', '    试验机制造', '411', NULL, '    指材料机械性能的试验机器和器械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2670, '4119', '    供应用仪表及其他通用仪器制造', '411', NULL, '    指电、气、水、油和热等类似气体或液体的供应过程中使用的计量仪表、自动调节或控制仪器及装置， 以及其他未列明的通用仪器仪表和仪表元器件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2671, '4121', '    环境监测专用仪器仪表制造', '412', NULL, '    指对水和空气中的污染物，以及噪声、放射性物质、电磁波等进行监测的专用仪器仪表的制造。');
INSERT INTO `sys_industry_2002` VALUES (2672, '4122', '    汽车及其他用计数仪表制造', '412', NULL, '    指汽车、船舶及工业生产用转数计、生产计数器、里程记录器及类似仪表的制造。');
INSERT INTO `sys_industry_2002` VALUES (2673, '4123', '    导航、气象及海洋专用仪器制造', '412', NULL, '    指用于气象、海洋、水文、天文、航海、航空等方面的导航、制导、测量仪器和仪表及类似装置的制造。      ');
INSERT INTO `sys_industry_2002` VALUES (2674, '4124', '    农林牧渔专用仪器仪表制造', '412', NULL, '    指农、林、牧、渔生产专用仪器、仪表及类似装置的制造。      ');
INSERT INTO `sys_industry_2002` VALUES (2675, '4125', '    地质勘探和地震专用仪器制造', '412', NULL, '    指地质勘探、钻采、地震等地球物理专用仪器、仪表及类似装置的制造。');
INSERT INTO `sys_industry_2002` VALUES (2676, '4126', '    教学专用仪器制造', '412', NULL, '    指专供教学示范或展览，而无其他用途的专用仪器的制造。');
INSERT INTO `sys_industry_2002` VALUES (2677, '4127', '    核子及核辐射测量仪器制造', '412', NULL, '    指专门用于核离子射线的测量或检验的仪器、装置、核辐射探测器等核专业用仪器仪表的制造。');
INSERT INTO `sys_industry_2002` VALUES (2678, '4128', '    电子测量仪器制造 ', '412', NULL, '    指用电子技术实现对被测对象（电子产品）的电参数定量检测装置的制造  。');
INSERT INTO `sys_industry_2002` VALUES (2679, '4129', '    其他专用仪器制造', '412', NULL, '    指用于纺织、电站热工仪表等其他未列明的专用仪器的制造，以及对专用仪器、仪表的修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2680, '4130', '  钟表与计时仪器制造', '413', NULL, '    指各种钟、表、钟表机芯、时间记录装置、计时器的制造，还包括装有钟表机芯或同步马达用以测量、记录或指示时间间隔的装置、定时开关，以及钟表零配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2681, '4141', '    光学仪器制造', '414', NULL, '    指用玻璃或其他材料（如石英、萤石、塑料或金属）制作的光学配件、装配好的光学元件、组合式光学显微镜，以及军用望远镜等光学仪器的制造。\n');
INSERT INTO `sys_industry_2002` VALUES (2682, '4142', '    眼镜制造', '414', NULL, '    指眼镜成镜、眼镜框架和零配件、眼镜镜片、角膜接触镜（隐形眼镜）及护理产品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2683, '4151', '    电影机械制造', '415', NULL, '    指各种类型或用途的电影摄影机、电影录音摄影机、影像放映机及电影辅助器材和配件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2684, '4152', '    幻灯及投影设备制造', '415', NULL, '    指将电路图形投射在有感光性的半导体材料上的幻灯、投影设备、器材装置及零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2685, '4153', '    照相机及器材制造', '415', NULL, '    指各种类型或用途的照相机，包括用以制备印刷板，用于水下或空中照相的照相机，以及照相机用闪光装置、摄影暗室装置和零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2686, '4154', '    复印和胶印设备制造', '415', NULL, '    指各种用途的复印设备，主要用于办公室的胶印设备、文字处理设备及零件的制造。');
INSERT INTO `sys_industry_2002` VALUES (2687, '4155', '    计算器及货币专用设备制造', '415', NULL, '     指金融、商业、交通及办公等使用的电子计算器、具有计算功能的数据记录、重现和显示机器，以及货币专用设备及类似机械的制造。');
INSERT INTO `sys_industry_2002` VALUES (2688, '4159', '    其他文化、办公用机械制造', '415', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2689, '4190', '    其他仪器仪表的制造及修理', '419', NULL, '    指上述未列明的仪器、仪表的制造与修理活动。');
INSERT INTO `sys_industry_2002` VALUES (2690, '4211', '    雕塑工艺品制造', '421', NULL, '    指以玉石、宝石、象牙、角、骨、贝壳等硬质材料，木、竹、椰壳、树根、软木等天然植物，以及石膏、泥、面、塑料等为原料，经雕刻、琢、磨、捏或塑等艺术加工而制成的各种供欣赏和实用的工艺品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2691, '4212', '    金属工艺品制造', '421', NULL, '     指以金、银、铜、铁、锡等各种金属为原料，经过制胎、浇铸、锻打、錾刻、搓丝、焊接、纺织、镶嵌、点兰、烧制、打磨、电镀等各种工艺加工制成的造型美观、花纹图案精致的工艺美术品的制作活动。');
INSERT INTO `sys_industry_2002` VALUES (2692, '4213', '    漆器工艺品制造', '421', NULL, '    指将半生漆、腰果漆加工调配成各种鲜艳的漆料，以木、纸、塑料、铜、布等作胎，采用推光、雕填、彩画、嵌镶、刻灰等传统工艺和现代漆器工艺进行的工艺制品的制作活动。');
INSERT INTO `sys_industry_2002` VALUES (2693, '4214', '    花画工艺品制造', '421', NULL, '    指以绢、丝、绒、纸、涤纶、塑料、羽毛、通草以及鲜花草等为原料，经造型设计、模压、剪贴、干燥等工艺精制而成的花、果、叶等人造花类工艺品，以画面出现、可以挂或摆的具有欣赏性、装饰性的画类工艺品的制作活动。');
INSERT INTO `sys_industry_2002` VALUES (2694, '4215', '    天然植物纤维编织工艺品制造', '421', NULL, '    指以竹、藤、棕、草、柳、葵、麻等天然植物纤维为材料，经编织或镶嵌而成具有造型艺术或图案花纹，以欣赏为主的工艺陈列品以及工艺实用品的制作活动。');
INSERT INTO `sys_industry_2002` VALUES (2695, '4216', '    抽纱刺绣工艺品制造', '421', NULL, '    指以棉、麻、丝、毛及人造纤维纺织品等为主要原料，经设计、刺绣、抽、拉、钩等工艺加工各种生活装饰用品，以及以纺织品为主要原料，经特殊手工工艺或民间工艺方法加工成各种具有较强装饰效果的生活用纺织品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2696, '4217', '    地毯、挂毯制造', '421', NULL, '    指以羊毛、丝、棉、麻及人造纤维等为原料，经手工编织、机织、栽绒等方式加工而成的各种具有装饰性的地面覆盖物或可用于悬挂、垫坐等用途的生活装饰用品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2697, '4218', '    珠宝首饰及有关物品的制造', '421', NULL, '    指以金、银、铂等贵金属及其合金以及钻石、宝石、玉石、翡翠、珍珠等为原料，经金属加工和连结组合、镶嵌等工艺加工制作各种图案的装饰品的制作活动。');
INSERT INTO `sys_industry_2002` VALUES (2698, '4219', '    其他工艺美术品制造', '421', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2699, '4221', '        制镜及类似品加工', '422', NULL, '    指以平板玻璃为材料，经对其进行镀银、镀铝，或冷、热加工后成型的日用制品的制造。');
INSERT INTO `sys_industry_2002` VALUES (2700, '4222', '    鬃毛加工、制刷及清扫工具的制造', '422', NULL, '    指用原毛加工成生产刷子类产品的成品毛的生产，或以成品毛和棕、金属丝、塑料丝等为原料加工制刷的生产，以及其他清扫工具的制造。');
INSERT INTO `sys_industry_2002` VALUES (2701, '4229', '    其他日用杂品制造', '422', NULL, '    指上述类别中均未列明的制伞及其他各种日常生活用杂品的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2702, '4230', '  煤制品制造', '423', NULL, '    指用烟煤、无烟煤、褐煤及其他各种煤炭制成的煤砖、煤球等固体燃料制品的活动。');
INSERT INTO `sys_industry_2002` VALUES (2703, '4240', '  核辐射加工', '424', NULL, '    指核技术与同位素技术的应用，由核辐照站利用核技术对原有产品改良、改变性质并使其增值的加工活动。');
INSERT INTO `sys_industry_2002` VALUES (2704, '4290', '     其他未列明的制造业', '429', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2705, '4310', '  金属废料和碎屑的加工处理', '431', NULL, '    指从各种废料[包括固体废料、废水（液）、废气等]中回收，并使之便于转化为新的原材料，或适于进一步加工为金属原料的金属废料和碎屑的再加工处理活动。');
INSERT INTO `sys_industry_2002` VALUES (2706, '4320', '  非金属废料和碎屑的加工处理', '432', NULL, '    指从各种废料[包括固体废料、废水（液）、废气等]中回收，或经过分类，使其适于进一步加工为新原料的非金属废料和碎屑的再加工处理活动。');
INSERT INTO `sys_industry_2002` VALUES (2707, '4411', '    火力发电 ', '441', NULL, '    指利用煤炭、石油、天然气等燃料燃烧产生的热能，通过火电动力装置转换成电能的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2708, '4412', '    水力发电', '441', NULL, '    指通过建设水电站将水能转换成电能的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2709, '4413', '    核力发电', '441', NULL, '    指利用核反应堆中重核裂变所释放出的热能转换成电能的生产活动。');
INSERT INTO `sys_industry_2002` VALUES (2710, '4419', '    其他能源发电', '441', NULL, '    指利用风力、地热、太阳能、潮汐能、生物能及其他未列明的发电活动。');
INSERT INTO `sys_industry_2002` VALUES (2711, '4420', '  电力供应', '442', NULL, '    指利用电网出售给用户电能的输送与分配活动，以及供电局的供电活动。');
INSERT INTO `sys_industry_2002` VALUES (2712, '4430', '  热力生产和供应', '443', NULL, '    指利用煤炭、油、燃气等能源，通过锅炉等装置生产蒸汽和热水，或外购蒸汽、热水进行供应销售、供热设施的维护和管理的活动。');
INSERT INTO `sys_industry_2002` VALUES (2713, '4500', '  燃气生产和供应业', '450', NULL, '    指利用煤炭、油、燃气等能源生产燃气，或外购液化石油气、天然气等燃气，并进行输配，向用户销售燃气的活动，以及对煤气、液化石油气、天然气输配及使用过程中的维修和管理活动。但不包括专门从事罐装液化石油气零售业务的活动。');
INSERT INTO `sys_industry_2002` VALUES (2714, '4610', '  自来水的生产和供应', '461', NULL, '    指将天然水（地下水、地表水）经过蓄集、净化达到生活饮用水或其他用水标准，并向居民家庭、企业和其他用户供应的活动。');
INSERT INTO `sys_industry_2002` VALUES (2715, '4620', '  污水处理及其再生利用', '462', NULL, '    指对污水的收集、处理及净化后的再利用活动。');
INSERT INTO `sys_industry_2002` VALUES (2716, '4690', '  其他水的处理、利用与分配', '469', NULL, '    指将海水淡化处理，达到可以使用标准的生产活动，以及对雨水、微咸水等类似水进行收集、处理和利用活动。');
INSERT INTO `sys_industry_2002` VALUES (2717, '4710', '  房屋工程建筑', '471', NULL, '    指房屋主体工程的施工活动。不包括主体工程施工前的工程准备活动。');
INSERT INTO `sys_industry_2002` VALUES (2718, '4721', '    铁路、道路、隧道和桥梁工程建筑    ', '472', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2719, '4722', '    水利和港口工程建筑', '472', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2720, '4723', '    工矿工程建筑', '472', NULL, '    指除厂房外的矿山和工厂生产设施、设备的施工和安装，以及海洋石油平台的施工。');
INSERT INTO `sys_industry_2002` VALUES (2721, '4724', '    架线和管道工程建筑', '472', NULL, '    指建筑物外的架线、管道和设备的施工。');
INSERT INTO `sys_industry_2002` VALUES (2722, '4729', '    其他土木工程建筑', '472', NULL, NULL);
INSERT INTO `sys_industry_2002` VALUES (2723, '4800', '    建筑安装业', '480', NULL, '    指建筑物主体工程竣工后，建筑物内各种设备的安装活动，以及施工中的线路敷设和管道安装。不包括工程收尾的装饰，如对墙面、地板、天花板、门窗等处理活动。');
INSERT INTO `sys_industry_2002` VALUES (2724, '4900', '    建筑装饰业', '490', NULL, '    指对建筑工程后期的装饰、装修和清理活动，以及对居室的装修活动。');
INSERT INTO `sys_industry_2002` VALUES (2725, '5010', '  工程准备', '501', NULL, '    指房屋、土木工程建筑施工前的准备活动。');
INSERT INTO `sys_industry_2002` VALUES (2726, '5020', '    提供施工设备服务', '502', NULL, '    指为建筑工程提供配有操作人员的施工设备的服务。');
INSERT INTO `sys_industry_2002` VALUES (2727, '5090', '    其他未列明的建筑活动', '509', NULL, '    指上述未列明的其他工程建筑活动。');
INSERT INTO `sys_industry_2002` VALUES (2728, '5110', '  铁路旅客运输', '511', NULL, ' ');
